import React, { useState, useRef } from "react";
import axios from "axios";

export default function WomenChildComplaint() {
  const [recording, setRecording] = useState(false);
  const [complaintText, setComplaintText] = useState("");
  const [token, setToken] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const recognitionRef = useRef(null); // hold Web Speech API instance

  // üéôÔ∏è Single Speak button (toggle start/stop). Text appears in textarea automatically
  const handleSpeechToText = () => {
    const SpeechRecognition =
      window.SpeechRecognition || window.webkitSpeechRecognition;

    if (!SpeechRecognition) {
      alert("Speech recognition is not supported in this browser. Please use Chrome.");
      return;
    }

    // If already recording ‚Üí stop
    if (recording) {
      if (recognitionRef.current) {
        try {
          recognitionRef.current.stop();
        } catch (e) {
          console.warn("Recognition already stopped.");
        }
      }
      setRecording(false);
      return;
    }

    // New instance
    const recognition = new SpeechRecognition();
    recognition.lang = "en-US";        // change to "hi-IN" if you want Hindi
    recognition.continuous = true;     // keep listening until user stops
    recognition.interimResults = true; // show interim text

    recognition.onresult = (event) => {
      let transcript = "";
      for (let i = 0; i < event.results.length; i++) {
        transcript += event.results[i][0].transcript + " ";
      }
      setComplaintText(transcript.trim());
    };

    recognition.onerror = (err) => {
      console.error("Speech Recognition Error:", err);
      if (err.error === "no-speech") {
        alert("No speech detected. Please try again and speak closer to the mic.");
      } else if (err.error === "not-allowed") {
        alert("Microphone permission is blocked. Please allow mic access in your browser settings.");
      } else if (err.error === "aborted") {
        // user quickly toggled or browser aborted ‚Äî keep it silent
      } else {
        alert("Speech recognition error: " + err.error);
      }
      setRecording(false);
    };

    recognition.onend = () => {
      setRecording(false);
    };

    // Start
    recognition.start();
    recognitionRef.current = recognition;
    setRecording(true);
  };

  // üì§ Submit Complaint ‚Äî attaches live location automatically
  const handleSubmit = async () => {
    if (!complaintText.trim()) {
      alert("Please speak or type your complaint.");
      return;
    }

    setIsSubmitting(true);

    // Helper to actually send the request
    const submitToServer = async (coords) => {
      try {
        const formData = new FormData();
        formData.append("category", "women-child"); // server can map this to "Police" dept
        formData.append("text", complaintText);

        if (coords) {
          formData.append("latitude", coords.latitude);
          formData.append("longitude", coords.longitude);
        }

        const res = await axios.post("http://localhost:5000/api/complaint", formData, {
          headers: { "Content-Type": "multipart/form-data" },
        });

        if (res.data?.success) {
          setToken(res.data.token);
          setComplaintText("");
          alert(`Complaint submitted successfully${coords ? " with location" : ""}!`);
        } else {
          alert(res.data?.message || "Failed to submit complaint.");
        }
      } catch (err) {
        console.error(err);
        alert("Error submitting complaint.");
      } finally {
        setIsSubmitting(false);
      }
    };

    // Try to get location (works on HTTPS or http://localhost only)
    if (!navigator.geolocation) {
      // No geolocation support
      await submitToServer(null);
      return;
    }

    navigator.geolocation.getCurrentPosition(
      async (position) => {
        const { latitude, longitude } = position.coords;
        await submitToServer({ latitude, longitude });
      },
      async (error) => {
        console.warn("Geolocation error:", error);
        // Ask user if they want to proceed without location
        const proceed = window.confirm(
          "We couldn't access your location (denied/timeout). Submit complaint without location?"
        );
        if (proceed) {
          await submitToServer(null);
        } else {
          setIsSubmitting(false);
        }
      },
      {
        enableHighAccuracy: true,
        timeout: 10000,
        maximumAge: 0,
      }
    );
  };

  return (
    <div className="max-w-xl mx-auto p-6 bg-white rounded-2xl shadow-lg">
      <h2 className="text-2xl font-bold mb-4 text-center text-gray-800">
        üë©‚Äçüëß Women & Child Complaint
      </h2>

      {/* üéôÔ∏è Speak (single button) */}
      <button
        onClick={handleSpeechToText}
        className={`w-full px-4 py-2 rounded-lg mb-3 font-semibold ${
          recording ? "bg-red-500" : "bg-purple-600"
        } text-white`}
      >
        {recording ? "‚èπ Stop Speaking" : "üó£ Speak Complaint"}
      </button>

      {/* üìù Complaint Box */}
      <textarea
        placeholder="Type or speak your complaint here..."
        value={complaintText}
        onChange={(e) => setComplaintText(e.target.value)}
        className="w-full border rounded-lg p-3 mb-3 h-32 focus:ring-2 focus:ring-blue-400"
      />

      {/* üì§ Submit */}
      <button
        onClick={handleSubmit}
        disabled={isSubmitting}
        className={`w-full ${
          isSubmitting ? "bg-gray-400" : "bg-green-600"
        } text-white px-4 py-2 rounded-lg font-semibold`}
      >
        {isSubmitting ? "Submitting..." : "‚úÖ Submit Complaint"}
      </button>

      {/* üé´ Token Display */}
      {token && (
        <div className="mt-4 p-4 border rounded-lg bg-gray-100 shadow-sm">
          <p className="font-semibold text-green-700">‚úÖ Complaint Submitted!</p>
          <p className="text-gray-700">
            Your Token Number:{" "}
            <span className="font-mono text-lg text-black">{token}</span>
          </p>
        </div>
      )}

      <p className="text-xs text-gray-500 mt-3">
        Note: Location is captured automatically on submit (works on HTTPS or localhost).
      </p>
    </div>
  );
}
