import React, { useState, useRef } from "react";
import axios from "axios";

export default function WomenChildComplaint() {
  const [recording, setRecording] = useState(false);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [audioBlob, setAudioBlob] = useState(null);
  const [complaintText, setComplaintText] = useState("");
  const [token, setToken] = useState(null);
  const audioRef = useRef(null);

  // 🎤 Voice Recording (Save Audio)
  const handleVoiceRecord = async () => {
    if (!recording) {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
        const recorder = new MediaRecorder(stream);
        const chunks = [];

        recorder.ondataavailable = (e) => chunks.push(e.data);
        recorder.onstop = () => {
          const blob = new Blob(chunks, { type: "audio/wav" });
          setAudioBlob(blob);
          if (audioRef.current) {
            audioRef.current.src = URL.createObjectURL(blob);
          }
        };

        recorder.start();
        setMediaRecorder(recorder);
        setRecording(true);
      } catch (err) {
        console.error("Mic error:", err);
        alert("Microphone access is required!");
      }
    } else {
      mediaRecorder.stop();
      setRecording(false);
    }
  };

  // 🗣️ Speech Recognition (Direct Text)
  const handleSpeechToText = () => {
    const SpeechRecognition =
      window.SpeechRecognition || window.webkitSpeechRecognition;

    if (!SpeechRecognition) {
      alert("Speech recognition not supported in your browser.");
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.lang = "hi-IN"; // Hindi; use "en-US" for English
    recognition.continuous = false;
    recognition.interimResults = false;

    recognition.onresult = (event) => {
      const spokenText = event.results[0][0].transcript;
      console.log("🎤 User said:", spokenText);
      setComplaintText((prev) => prev + " " + spokenText);
    };

    recognition.start();
  };

  // 📤 Submit Complaint
  const handleSubmit = async () => {
    if (!complaintText && !audioBlob) {
      alert("Please provide text or voice complaint.");
      return;
    }

    try {
      const formData = new FormData();
      formData.append("category", "women-child");

      if (complaintText) formData.append("text", complaintText);
      if (audioBlob) formData.append("voice", audioBlob, "complaint.wav");

      const res = await axios.post("http://localhost:5000/api/complaint", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      if (res.data.success) {
        setToken(res.data.token);
        setComplaintText("");
        setAudioBlob(null);
        alert("Complaint submitted successfully!");
      }
    } catch (err) {
      console.error(err);
      alert("Error submitting complaint");
    }
  };

  return (
    <div className="flex justify-center mt-10">
      <div className="w-full max-w-lg bg-white shadow-lg rounded-2xl p-6">
        <h2 className="text-2xl font-bold text-center mb-6">
          👩‍👧 Women & Child Complaint
        </h2>

        {/* 🎤 Recording Buttons */}
        <div className="flex gap-3 mb-4">
          <button
            onClick={handleVoiceRecord}
            className={`flex-1 px-4 py-2 rounded-lg ${
              recording ? "bg-red-500" : "bg-blue-500"
            } text-white`}
          >
            {recording ? "⏹ Stop Recording" : "🎤 Record Voice"}
          </button>

          <button
            onClick={handleSpeechToText}
            className="flex-1 bg-purple-600 text-white px-4 py-2 rounded-lg"
          >
            🗣️ Speak to Text
          </button>
        </div>

        {/* 🎧 Playback */}
        {audioBlob && (
          <div className="mb-4">
            <p className="text-sm font-medium">🎧 Recorded Voice:</p>
            <audio ref={audioRef} controls className="mt-1 w-full" />
          </div>
        )}

        {/* 📝 Text Complaint */}
        <textarea
          placeholder="Type or speak your complaint..."
          value={complaintText}
          onChange={(e) => setComplaintText(e.target.value)}
          className="w-full border rounded-lg p-3 mb-4 h-28 focus:outline-none focus:ring-2 focus:ring-blue-400"
        />

        {/* 📤 Submit */}
        <button
          onClick={handleSubmit}
          className="w-full bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-700"
        >
          ✅ Submit Complaint
        </button>

        {/* 🎫 Show Token */}
        {token && (
          <div className="mt-6 p-4 border rounded-lg bg-gray-50 text-center">
            <p className="font-semibold text-green-700">
              ✅ Complaint Submitted!
            </p>
            <p>
              Your Token Number:{" "}
              <span className="font-mono text-lg text-blue-600">{token}</span>
            </p>
          </div>
        )}
      </div>
    </div>
  );
}
