import React, { useEffect, useState } from "react";
import { Search, Filter, Eye, Download, RefreshCw, Calendar, Clock, User, MapPin, Building, AlertTriangle } from "lucide-react";

const AdminDashboard = () => {
  const [complaints, setComplaints] = useState([]);
  const [filteredComplaints, setFilteredComplaints] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");
  const [statusFilter, setStatusFilter] = useState("All");
  const [departmentFilter, setDepartmentFilter] = useState("All");
  const [dateFilter, setDateFilter] = useState("All");
  const [selectedComplaint, setSelectedComplaint] = useState(null);
  const [showModal, setShowModal] = useState(false);

  const fetchComplaints = async () => {
    setLoading(true);
    try {
      const res = await fetch("/api/admin/complaints");
      const data = await res.json();
      if (data.success) {
        setComplaints(data.complaints);
        setFilteredComplaints(data.complaints);
      } else {
        console.error(data.message);
      }
    } catch (err) {
      console.error("Error fetching complaints:", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchComplaints();
  }, []);

  // Filter complaints
  useEffect(() => {
    let filtered = complaints;

    if (searchTerm) {
      filtered = filtered.filter(c =>
        c.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        c.location?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        c.department?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        c.description?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        c.id?.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    if (statusFilter !== "All") {
      filtered = filtered.filter(c => c.status === statusFilter);
    }

    if (departmentFilter !== "All") {
      filtered = filtered.filter(c => c.department === departmentFilter);
    }

    if (dateFilter !== "All") {
      const now = new Date();
      filtered = filtered.filter(c => {
        const complaintDate = new Date(c.timestamp);
        switch (dateFilter) {
          case "Today":
            return complaintDate.toDateString() === now.toDateString();
          case "This Week":
            const weekAgo = new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000);
            return complaintDate >= weekAgo;
          case "This Month":
            return complaintDate.getMonth() === now.getMonth() && 
                   complaintDate.getFullYear() === now.getFullYear();
          default:
            return true;
        }
      });
    }

    setFilteredComplaints(filtered);
  }, [complaints, searchTerm, statusFilter, departmentFilter, dateFilter]);

  const handleStatusChange = async (id, newStatus) => {
    try {
      const res = await fetch("/api/admin/update_status", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ id, status: newStatus })
      });
      const data = await res.json();
      
      if (data.success) {
        setComplaints(prev =>
          prev.map(c => (c.id === id ? { ...c, status: newStatus } : c))
        );
      } else {
        alert(data.message);
      }
    } catch (err) {
      console.error("Error updating status:", err);
    }
  };

  const getStatusColor = status => {
    switch (status) {
      case "Resolved":
        return "bg-green-100 text-green-800 border-green-200";
      case "Processing":
        return "bg-yellow-100 text-yellow-800 border-yellow-200";
      case "Pending":
        return "bg-red-100 text-red-800 border-red-200";
      default:
        return "bg-gray-100 text-gray-800 border-gray-200";
    }
  };

  const getUrgencyColor = urgency => {
    switch (urgency?.toLowerCase()) {
      case "high":
        return "bg-red-500 text-white";
      case "medium":
        return "bg-yellow-500 text-white";
      case "low":
        return "bg-green-500 text-white";
      default:
        return "bg-blue-500 text-white";
    }
  };

  const getPriorityIcon = urgency => {
    switch (urgency?.toLowerCase()) {
      case "high":
        return <AlertTriangle className="w-4 h-4" />;
      default:
        return null;
    }
  };

  // ‚úÖ Google Maps clickable location
  const renderLocation = (c) => {
    if (c.latitude && c.longitude) {
      const url = `https://www.google.com/maps?q=${c.latitude},${c.longitude}`;
      return (
        <a href={url} target="_blank" rel="noopener noreferrer" className="text-blue-600 hover:underline">
          {c.latitude}, {c.longitude} üìç
        </a>
      );
    } else if (c.location) {
      const url = `https://www.google.com/maps/search/?api=1&query=${encodeURIComponent(c.location)}`;
      return (
        <a href={url} target="_blank" rel="noopener noreferrer" className="text-blue-600 hover:underline">
          {c.location} üìç
        </a>
      );
    }
    return <span>N/A</span>;
  };

  // Unique departments
  const departments = [...new Set(complaints.map(c => c.department).filter(Boolean))];

  // Stats
  const stats = {
    total: complaints.length,
    pending: complaints.filter(c => c.status === "Pending").length,
    processing: complaints.filter(c => c.status === "Processing").length,
    resolved: complaints.filter(c => c.status === "Resolved").length,
    today: complaints.filter(c => {
      const today = new Date().toDateString();
      return new Date(c.timestamp).toDateString() === today;
    }).length,
    thisWeek: complaints.filter(c => {
      const weekAgo = new Date(Date.now() - 7 * 24 * 60 * 60 * 1000);
      return new Date(c.timestamp) >= weekAgo;
    }).length
  };

  const exportToCSV = () => {
    const headers = ["ID", "Name", "Location", "Department", "Status", "Urgency", "Description", "Timestamp", "Token"];
    const csvContent = [
      headers.join(","),
      ...filteredComplaints.map(c => [
        c.id,
        c.name,
        c.location,
        c.department || "Unknown",
        c.status,
        c.urgency || "Normal",
        `"${c.description?.replace(/"/g, '""') || ''}"`,
        new Date(c.timestamp).toLocaleString(),
        c.token
      ].join(","))
    ].join("\n");

    const blob = new Blob([csvContent], { type: "text/csv" });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = `complaints_${new Date().toISOString().split('T')[0]}.csv`;
    a.click();
    window.URL.revokeObjectURL(url);
  };

  const viewComplaint = (complaint) => {
    setSelectedComplaint(complaint);
    setShowModal(true);
  };

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      {/* ... header, stats, filters code same ... */}

      {/* Table */}
      <div className="bg-white rounded-xl shadow-sm border border-gray-200 overflow-hidden">
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Complaint Details
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Location & Department
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Status & Priority
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Timestamp
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Actions
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {loading ? (
                <tr>
                  <td colSpan="5" className="px-6 py-4 text-center">
                    <div className="flex justify-center items-center">
                      <RefreshCw className="w-6 h-6 animate-spin text-blue-600" />
                      <span className="ml-2 text-gray-500">Loading...</span>
                    </div>
                  </td>
                </tr>
              ) : filteredComplaints.length === 0 ? (
                <tr>
                  <td colSpan="5" className="px-6 py-4 text-center text-gray-500">
                    No complaints found matching your filters.
                  </td>
                </tr>
              ) : (
                filteredComplaints.map((complaint) => (
                  <tr key={complaint.id} className="hover:bg-gray-50 transition-colors">
                    <td className="px-6 py-4">
                      <div className="flex items-start space-x-3">
                        <div className="flex-shrink-0">
                          <div className="w-10 h-10 bg-blue-100 rounded-full flex items-center justify-center">
                            <User className="w-5 h-5 text-blue-600" />
                          </div>
                        </div>
                        <div className="min-w-0 flex-1">
                          <p className="text-sm font-medium text-gray-900">
                            {complaint.name}
                          </p>
                          <p className="text-xs text-gray-500">
                            ID: {complaint.id}
                          </p>
                          <p className="text-xs text-gray-400 mt-1 truncate max-w-xs">
                            {complaint.description}
                          </p>
                        </div>
                      </div>
                    </td>
                    <td className="px-6 py-4">
                      <div className="text-sm">
                        <p className="text-gray-900 flex items-center">
                          <MapPin className="w-4 h-4 mr-1 text-gray-400" />
                          {renderLocation(complaint)}
                        </p>
                        <p className="text-gray-500 flex items-center mt-1">
                          <Building className="w-4 h-4 mr-1 text-gray-400" />
                          {complaint.department || "Unknown"}
                        </p>
                      </div>
                    </td>
                    <td className="px-6 py-4">
                      <div className="flex flex-col space-y-2">
                        <select
                          value={complaint.status}
                          onChange={(e) => handleStatusChange(complaint.id, e.target.value)}
                          className={`text-xs px-2 py-1 rounded-full border font-medium ${getStatusColor(complaint.status)}`}
                        >
                          <option value="Pending">Pending</option>
                          <option value="Processing">Processing</option>
                          <option value="Resolved">Resolved</option>
                        </select>
                        {complaint.urgency && (
                          <div className={`flex items-center text-xs px-2 py-1 rounded-full ${getUrgencyColor(complaint.urgency)}`}>
                            {getPriorityIcon(complaint.urgency)}
                            <span className="ml-1">{complaint.urgency}</span>
                          </div>
                        )}
                      </div>
                    </td>
                    <td className="px-6 py-4 text-sm text-gray-500">
                      <div className="flex flex-col">
                        <span>{new Date(complaint.timestamp).toLocaleDateString()}</span>
                        <span className="text-xs text-gray-400">
                          {new Date(complaint.timestamp).toLocaleTimeString()}
                        </span>
                      </div>
                    </td>
                    <td className="px-6 py-4">
                      <button
                        onClick={() => viewComplaint(complaint)}
                        className="flex items-center text-blue-600 hover:text-blue-800 transition-colors"
                      >
                        <Eye className="w-4 h-4 mr-1" />
                        View
                      </button>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      </div>

      {/* ... modal code same ... */}
    </div>
  );
};

export default AdminDashboard;
