import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { ArrowLeft, Upload, Camera, FileText, AlertCircle, CheckCircle, XCircle, Info } from 'lucide-react';

const WasteClassification = () => {
  const navigate = useNavigate();
  const [selectedFile, setSelectedFile] = useState(null);
  const [preview, setPreview] = useState(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [result, setResult] = useState(null);
  const [activeTab, setActiveTab] = useState('upload');

  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedFile(file);
      const reader = new FileReader();
      reader.onload = (e) => setPreview(e.target.result);
      reader.readAsDataURL(file);
    }
  };

  const handleAnalyze = async () => {
    if (!selectedFile) return;
    
    setIsAnalyzing(true);
    
    // Simulate API call to waste classification service
    setTimeout(() => {
      const mockResults = [
        {
          type: 'Biodegradable',
          confidence: 95,
          description: 'This waste will decompose naturally in the environment',
          disposal: 'Can be composted or disposed in organic waste bins',
          icon: '��',
          color: 'text-green-600',
          bgColor: 'bg-green-100'
        },
        {
          type: 'Non-Biodegradable',
          confidence: 87,
          description: 'This waste will not decompose naturally',
          disposal: 'Should be recycled or disposed in appropriate waste bins',
          icon: '⚠️',
          color: 'text-red-600',
          bgColor: 'bg-red-100'
        },
        {
          type: 'Recyclable',
          confidence: 92,
          description: 'This material can be processed and reused',
          disposal: 'Place in recycling bins for proper processing',
          icon: '♻️',
          color: 'text-blue-600',
          bgColor: 'bg-blue-100'
        }
      ];
      
      setResult(mockResults[Math.floor(Math.random() * mockResults.length)]);
      setIsAnalyzing(false);
    }, 3000);
  };

  const resetAnalysis = () => {
    setSelectedFile(null);
    setPreview(null);
    setResult(null);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 via-pink-50 to-indigo-50 py-12 px-4">
      <div className="max-w-4xl mx-auto">
        {/* Header */}
        <div className="text-center mb-12">
          <button
            onClick={() => navigate('/')}
            className="inline-flex items-center px-4 py-2 bg-white/80 backdrop-blur-sm rounded-lg shadow-lg hover:bg-white transition-all duration-300 mb-6"
          >
            <ArrowLeft className="w-5 h-5 mr-2" />
            Back to Home
          </button>
          
          <h1 className="text-5xl font-bold bg-gradient-to-r from-purple-600 to-pink-600 bg-clip-text text-transparent mb-4">
            Waste Classification
          </h1>
          <p className="text-xl text-gray-600 max-w-2xl mx-auto">
            Upload an image of waste to determine if it's biodegradable, recyclable, or non-biodegradable
          </p>
        </div>

        {/* Main Content */}
        <div className="bg-white/80 backdrop-blur-sm rounded-3xl shadow-2xl p-8 border border-white/20">
          {/* Tab Navigation */}
          <div className="flex space-x-2 mb-8">
            <button
              onClick={() => setActiveTab('upload')}
              className={`flex-1 py-3 px-6 rounded-xl font-semibold transition-all duration-300 ${
                activeTab === 'upload'
                  ? 'bg-purple-600 text-white shadow-lg'
                  : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
              }`}
            >
              <Upload className="w-5 h-5 inline mr-2" />
              Upload Image
            </button>
            <button
              onClick={() => setActiveTab('camera')}
              className={`flex-1 py-3 px-6 rounded-xl font-semibold transition-all duration-300 ${
                activeTab === 'camera'
                  ? 'bg-purple-600 text-white shadow-lg'
                  : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
              }`}
            >
              <Camera className="w-5 h-5 inline mr-2" />
              Take Photo
            </button>
          </div>

          {/* Upload Section */}
          {activeTab === 'upload' && (
            <div className="text-center">
              {!preview ? (
                <div className="border-2 border-dashed border-purple-300 rounded-2xl p-12 hover:border-purple-400 transition-colors">
                  <Upload className="w-16 h-16 text-purple-400 mx-auto mb-4" />
                  <h3 className="text-xl font-semibold text-gray-700 mb-2">
                    Upload Waste Image
                  </h3>
                  <p className="text-gray-500 mb-6">
                    Drag and drop an image here, or click to browse
                  </p>
                  <input
                    type="file"
                    accept="image/*"
                    onChange={handleFileSelect}
                    className="hidden"
                    id="file-upload"
                  />
                  <label
                    htmlFor="file-upload"
                    className="inline-flex items-center px-6 py-3 bg-purple-600 text-white rounded-xl font-semibold hover:bg-purple-700 transition-colors cursor-pointer"
                  >
                    Choose File
                  </label>
                </div>
              ) : (
                <div className="space-y-6">
                  <div className="relative">
                    <img
                      src={preview}
                      alt="Preview"
                      className="w-full max-w-md mx-auto rounded-2xl shadow-lg"
                    />
                    <button
                      onClick={resetAnalysis}
                      className="absolute top-2 right-2 bg-red-500 text-white rounded-full p-2 hover:bg-red-600 transition-colors"
                    >
                      <XCircle className="w-5 h-5" />
                    </button>
                  </div>
                  
                  <button
                    onClick={handleAnalyze}
                    disabled={isAnalyzing}
                    className="px-8 py-4 bg-gradient-to-r from-purple-600 to-pink-600 text-white rounded-xl font-semibold text-lg hover:from-purple-700 hover:to-pink-700 transition-all duration-300 disabled:opacity-50 disabled:cursor-not-allowed shadow-lg"
                  >
                    {isAnalyzing ? 'Analyzing...' : 'Analyze Waste'}
                  </button>
                </div>
              )}
            </div>
          )}

          {/* Camera Section */}
          {activeTab === 'camera' && (
            <div className="text-center">
              <div className="bg-gray-100 rounded-2xl p-12">
                <Camera className="w-16 h-16 text-gray-400 mx-auto mb-4" />
                <h3 className="text-xl font-semibold text-gray-700 mb-2">
                  Camera Feature Coming Soon
                </h3>
                <p className="text-gray-500 mb-6">
                  This feature will be available in the next update
                </p>
                <button
                  onClick={() => setActiveTab('upload')}
                  className="px-6 py-3 bg-purple-600 text-white rounded-xl font-semibold hover:bg-purple-700 transition-colors"
                >
                  Use Upload Instead
                </button>
              </div>
            </div>
          )}

          {/* Results Section */}
          {result && (
            <div className="mt-8 p-6 bg-gradient-to-r from-purple-50 to-pink-50 rounded-2xl border border-purple-200">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-2xl font-bold text-gray-800">Analysis Results</h3>
                <span className={`text-4xl ${result.bgColor} p-3 rounded-full`}>
                  {result.icon}
                </span>
              </div>
              
              <div className="grid md:grid-cols-2 gap-6">
                <div>
                  <h4 className="font-semibold text-gray-700 mb-2">Waste Type</h4>
                  <p className={`text-xl font-bold ${result.color}`}>{result.type}</p>
                </div>
                
                <div>
                  <h4 className="font-semibold text-gray-700 mb-2">Confidence</h4>
                  <p className="text-xl font-bold text-gray-800">{result.confidence}%</p>
                </div>
              </div>
              
              <div className="mt-4">
                <h4 className="font-semibold text-gray-700 mb-2">Description</h4>
                <p className="text-gray-600">{result.description}</p>
              </div>
              
              <div className="mt-4">
                <h4 className="font-semibold text-gray-700 mb-2">Disposal Recommendation</h4>
                <p className="text-gray-600">{result.disposal}</p>
              </div>
              
              <div className="mt-6 flex space-x-4">
                <button
                  onClick={resetAnalysis}
                  className="px-6 py-3 bg-gray-600 text-white rounded-xl font-semibold hover:bg-gray-700 transition-colors"
                >
                  Analyze Another
                </button>
                <button
                  onClick={() => navigate('/')}
                  className="px-6 py-3 bg-purple-600 text-white rounded-xl font-semibold hover:bg-purple-700 transition-colors"
                >
                  Back to Home
                </button>
              </div>
            </div>
          )}

          {/* Info Section */}
          <div className="mt-8 p-6 bg-blue-50 rounded-2xl border border-blue-200">
            <div className="flex items-start space-x-3">
              <Info className="w-6 h-6 text-blue-600 mt-1 flex-shrink-0" />
              <div>
                <h4 className="font-semibold text-blue-800 mb-2">How It Works</h4>
                <ul className="text-blue-700 space-y-1 text-sm">
                  <li>• Upload a clear image of the waste item</li>
                  <li>• Our AI analyzes the image using advanced computer vision</li>
                  <li>• Get instant classification with disposal recommendations</li>
                  <li>• Learn about proper waste management practices</li>
                </ul>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default WasteClassification;
