import React, { useState, useRef } from "react";
import axios from "axios";

export default function WomenChildComplaint() {
  const [recording, setRecording] = useState(false);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [audioBlob, setAudioBlob] = useState(null);
  const [complaintText, setComplaintText] = useState("");
  const [token, setToken] = useState(null);
  const audioRef = useRef(null);

  // üé§ Start/Stop Recording
  const handleVoiceRecord = async () => {
    if (!recording) {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
        const recorder = new MediaRecorder(stream);
        const chunks = [];

        recorder.ondataavailable = (e) => chunks.push(e.data);
        recorder.onstop = () => {
          const blob = new Blob(chunks, { type: "audio/wav" });
          setAudioBlob(blob);
          if (audioRef.current) {
            audioRef.current.src = URL.createObjectURL(blob);
          }
        };

        recorder.start();
        setMediaRecorder(recorder);
        setRecording(true);
      } catch (err) {
        console.error("Mic error:", err);
        alert("Microphone access is required!");
      }
    } else {
      mediaRecorder.stop();
      setRecording(false);
    }
  };

  // üì§ Submit Complaint
  const handleSubmit = async () => {
    if (!complaintText && !audioBlob) {
      alert("Please provide text or voice complaint.");
      return;
    }

    try {
      const formData = new FormData();
      formData.append("category", "women-child");

      if (complaintText) formData.append("text", complaintText);
      if (audioBlob) formData.append("voice", audioBlob, "complaint.wav");

      const res = await axios.post("/api/complaint/women-child", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      if (res.data.success) {
        setToken(res.data.token);
        setComplaintText("");
        setAudioBlob(null);
        alert("Complaint submitted successfully!");
      }
    } catch (err) {
      console.error(err);
      alert("Error submitting complaint");
    }
  };

  return (
    <div className="page-container">
      <h2 className="text-xl font-bold mb-4">Women & Child Complaint</h2>

      {/* üé§ Record Button */}
      <button
        onClick={handleVoiceRecord}
        className={`px-4 py-2 rounded mb-3 ${recording ? "bg-red-500" : "bg-blue-500"} text-white`}
      >
        {recording ? "‚èπ Stop Recording" : "üé§ Start Recording"}
      </button>

      {/* üéß Playback */}
      {audioBlob && (
        <div className="mb-3">
          <p className="text-sm">Recorded Voice:</p>
          <audio ref={audioRef} controls />
        </div>
      )}

      {/* üìù Text Complaint */}
      <textarea
        placeholder="Type your complaint here..."
        value={complaintText}
        onChange={(e) => setComplaintText(e.target.value)}
        className="w-full border rounded p-2 mb-3"
      />

      {/* üì§ Submit */}
      <button
        onClick={handleSubmit}
        className="bg-green-600 text-white px-4 py-2 rounded"
      >
        Submit Complaint
      </button>

      {/* üé´ Show Token */}
      {token && (
        <div className="mt-4 p-3 border rounded bg-gray-100">
          <p className="font-semibold">‚úÖ Complaint Submitted!</p>
          <p>Your Token Number: <span className="font-mono">{token}</span></p>
        </div>
      )}
    </div>
  );
}
