import React, { useState, useRef } from "react";
import axios from "axios";

export default function WomenChildComplaint() {
  const [recording, setRecording] = useState(false);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [audioBlob, setAudioBlob] = useState(null);
  const [complaintText, setComplaintText] = useState("");
  const [token, setToken] = useState(null);
  const [listening, setListening] = useState(false);

  const audioRef = useRef(null);
  const recognitionRef = useRef(null);

  // 🎤 Start/Stop Recording (Voice Save)
  const handleVoiceRecord = async () => {
    if (!recording) {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
        const recorder = new MediaRecorder(stream);
        const chunks = [];

        recorder.ondataavailable = (e) => chunks.push(e.data);
        recorder.onstop = () => {
          const blob = new Blob(chunks, { type: "audio/wav" });
          setAudioBlob(blob);
          if (audioRef.current) {
            audioRef.current.src = URL.createObjectURL(blob);
          }
        };

        recorder.start();
        setMediaRecorder(recorder);
        setRecording(true);
      } catch (err) {
        console.error("Mic error:", err);
        alert("Microphone access is required!");
      }
    } else {
      mediaRecorder.stop();
      setRecording(false);
    }
  };

  // 🗣️ Start Speech-to-Text
  const handleSpeechToText = () => {
    const SpeechRecognition =
      window.SpeechRecognition || window.webkitSpeechRecognition;

    if (!SpeechRecognition) {
      alert("Speech recognition not supported in this browser.");
      return;
    }

    if (listening) {
      console.log("Already listening...");
      return; // 🔥 prevent double start
    }

    const recognition = new SpeechRecognition();
    recognition.lang = "hi-IN"; // Hindi (English chahiye to "en-US")
    recognition.continuous = true;
    recognition.interimResults = true;

    recognition.onresult = (event) => {
      let spokenText = "";
      for (let i = event.resultIndex; i < event.results.length; i++) {
        spokenText += event.results[i][0].transcript;
      }
      console.log("🎤 Bola gaya:", spokenText);

      setComplaintText((prev) =>
        prev ? prev + " " + spokenText : spokenText
      );
    };

    recognition.onerror = (err) => {
      console.error("Speech Recognition Error:", err.error);
    };

    recognition.onend = () => {
      if (listening) {
        console.log("Recognition ended. Restarting...");
        recognition.start(); // 🔁 restart if still listening
      }
    };

    recognition.start();
    recognitionRef.current = recognition;
    setListening(true);
  };

  // 🛑 Stop Speech-to-Text
  const stopSpeechToText = () => {
    if (recognitionRef.current) {
      recognitionRef.current.onend = null; // 🔥 stop ke baad restart na ho
      recognitionRef.current.stop();
      recognitionRef.current = null;
    }
    setListening(false);
  };

  // 📤 Submit Complaint
  const handleSubmit = async () => {
    if (!complaintText && !audioBlob) {
      alert("Please provide text or voice complaint.");
      return;
    }

    try {
      const formData = new FormData();
      formData.append("category", "women-child");

      if (complaintText) formData.append("text", complaintText);
      if (audioBlob) formData.append("voice", audioBlob, "complaint.wav");

      const res = await axios.post("http://localhost:5000/api/complaint", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      if (res.data.success) {
        setToken(res.data.token);
        setComplaintText("");
        setAudioBlob(null);
        alert("✅ Complaint submitted successfully!");
      }
    } catch (err) {
      console.error(err);
      alert("❌ Error submitting complaint");
    }
  };

  return (
    <div className="max-w-xl mx-auto p-6 bg-white rounded-2xl shadow-lg">
      <h2 className="text-2xl font-bold mb-4 text-center text-gray-800">
        👩‍👧 Women & Child Complaint
      </h2>

      {/* 🎤 Voice Recording */}
      <button
        onClick={handleVoiceRecord}
        className={`w-full px-4 py-2 rounded-lg mb-3 font-semibold ${
          recording ? "bg-red-500" : "bg-blue-500"
        } text-white`}
      >
        {recording ? "⏹ Stop Recording" : "🎤 Start Recording"}
      </button>

      {/* 🎧 Playback */}
      {audioBlob && (
        <div className="mb-3">
          <p className="text-sm font-medium">🔊 Recorded Voice:</p>
          <audio ref={audioRef} controls className="w-full mt-1" />
        </div>
      )}

      {/* 🗣️ Speech to Text */}
      {!listening ? (
        <button
          onClick={handleSpeechToText}
          className="w-full bg-purple-600 text-white px-4 py-2 rounded-lg mb-3 font-semibold"
        >
          🗣️ Start Speaking
        </button>
      ) : (
        <button
          onClick={stopSpeechToText}
          className="w-full bg-red-600 text-white px-4 py-2 rounded-lg mb-3 font-semibold"
        >
          🛑 Stop Listening
        </button>
      )}

      {/* 📝 Text Complaint */}
      <textarea
        placeholder="Type or speak your complaint here..."
        value={complaintText}
        onChange={(e) => setComplaintText(e.target.value)}
        className="w-full border rounded-lg p-3 mb-3 h-32 focus:ring-2 focus:ring-blue-400"
      />

      {/* 📤 Submit */}
      <button
        onClick={handleSubmit}
        className="w-full bg-green-600 text-white px-4 py-2 rounded-lg font-semibold"
      >
        ✅ Submit Complaint
      </button>

      {/* 🎫 Token Display */}
      {token && (
        <div className="mt-4 p-4 border rounded-lg bg-gray-100 shadow-sm">
          <p className="font-semibold text-green-700">✅ Complaint Submitted!</p>
          <p className="text-gray-700">
            Your Token Number:{" "}
            <span className="font-mono text-lg text-black">{token}</span>
          </p>
        </div>
      )}
    </div>
  );
}
