import React, { useState, useRef } from "react";
import axios from "axios";

export default function WomenChildComplaint() {
  const [recording, setRecording] = useState(false);
  const [complaintText, setComplaintText] = useState("");
  const [token, setToken] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const recognitionRef = useRef(null);

  // üéôÔ∏è Speech-to-text
  const handleSpeechToText = () => {
    // Check for browser support
    const SpeechRecognition = 
      window.SpeechRecognition || 
      window.webkitSpeechRecognition ||
      window.mozSpeechRecognition ||
      window.msSpeechRecognition;

    if (!SpeechRecognition) {
      alert("Speech recognition is not supported in this browser. Please use Chrome, Edge, or Safari.");
      return;
    }

    if (recording) {
      // Stop recording
      if (recognitionRef.current) {
        try {
          recognitionRef.current.stop();
          console.log("üõë Stopping speech recognition...");
        } catch (e) {
          console.warn("Recognition already stopped.");
        }
      }
      setRecording(false);
      return;
    }

    // Start recording
    console.log("üéôÔ∏è Starting speech recognition...");
    
    const recognition = new SpeechRecognition();
    
    // Configure recognition settings
    recognition.lang = "hi-IN"; // Hindi language for better Indian accent recognition
    recognition.continuous = true;
    recognition.interimResults = true;
    recognition.maxAlternatives = 1;

    // Handle speech results
    recognition.onresult = (event) => {
      let finalTranscript = "";
      let interimTranscript = "";

      for (let i = event.resultIndex; i < event.results.length; i++) {
        const transcript = event.results[i][0].transcript;
        if (event.results[i].isFinal) {
          finalTranscript += transcript + " ";
        } else {
          interimTranscript += transcript;
        }
      }

      // Update complaint text with final results
      if (finalTranscript) {
        setComplaintText(prev => prev + " " + finalTranscript.trim());
        console.log("‚úÖ Final transcript:", finalTranscript);
      }
      
      // Show interim results in console for debugging
      if (interimTranscript) {
        console.log("üéØ Interim transcript:", interimTranscript);
      }
    };

    // Handle errors
    recognition.onerror = (err) => {
      console.error("‚ùå Speech Recognition Error:", err);
      
      switch (err.error) {
        case "no-speech":
          alert("No speech detected. Please try again and speak clearly into the microphone.");
          break;
        case "not-allowed":
          alert("Microphone permission denied. Please allow microphone access in your browser settings and refresh the page.");
          break;
        case "network":
          alert("Network error occurred. Please check your internet connection.");
          break;
        case "audio-capture":
          alert("No microphone found. Please connect a microphone and try again.");
          break;
        case "service-not-allowed":
          alert("Speech recognition service not allowed. Please try refreshing the page.");
          break;
        default:
          alert(`Speech recognition error: ${err.error}. Please try again.`);
      }
      
      setRecording(false);
    };

    // Handle recognition end
    recognition.onend = () => {
      console.log("üèÅ Speech recognition ended");
      setRecording(false);
    };

    // Handle recognition start
    recognition.onstart = () => {
      console.log("üöÄ Speech recognition started");
      setRecording(true);
    };

    // Handle audio start
    recognition.onaudiostart = () => {
      console.log("üéµ Audio capture started");
    };

    // Handle audio end
    recognition.onaudioend = () => {
      console.log("üîá Audio capture ended");
    };

    // Handle speech start
    recognition.onspeechstart = () => {
      console.log("üó£Ô∏è Speech detected");
    };

    // Handle speech end
    recognition.onspeechend = () => {
      console.log("ü§ê Speech ended");
    };

    try {
      recognition.start();
      recognitionRef.current = recognition;
      console.log("‚úÖ Speech recognition started successfully");
    } catch (error) {
      console.error("‚ùå Failed to start speech recognition:", error);
      alert("Failed to start speech recognition. Please try refreshing the page.");
      setRecording(false);
    }
  };

  // üì§ Submit Complaint
  const handleSubmit = async () => {
    if (!complaintText.trim()) {
      alert("Please speak or type your complaint.");
      return;
    }

    setIsSubmitting(true);

    const submitToServer = async (coords) => {
      try {
        const formData = new FormData();
        formData.append("text", complaintText);
        formData.append("category", "women-child");

        if (coords) {
          formData.append("latitude", coords.latitude);
          formData.append("longitude", coords.longitude);
        }

        // Debug logging
        console.log("üîç Submitting complaint with text:", complaintText);
        console.log("üîç FormData contents:");
        for (let [key, value] of formData.entries()) {
          console.log(`  ${key}: ${value}`);
        }

        const res = await axios.post(
          "http://localhost:5000/api/complaint/women-child", // ‚úÖ endpoint
          formData,
          { headers: { "Content-Type": "multipart/form-data" } }
        );

        console.log("üîç Backend response:", res.data);

        if (res.data?.success) {
          setToken(res.data.token);
          setComplaintText("");
          alert(
            `Complaint submitted successfully${coords ? " with location" : ""}!`
          );
        } else {
          alert(res.data?.message || "Failed to submit complaint.");
        }
      } catch (err) {
        console.error("‚ùå Error submitting complaint:", err);
        alert("Error submitting complaint.");
      } finally {
        setIsSubmitting(false);
      }
    };

    // üìç Location capture
    if (!navigator.geolocation) {
      await submitToServer(null);
      return;
    }

    navigator.geolocation.getCurrentPosition(
      async (position) => {
        const { latitude, longitude } = position.coords;

        if (!latitude || !longitude || latitude === 0 || longitude === 0) {
          alert("Unable to fetch accurate location. Please try again outdoors.");
          setIsSubmitting(false);
          return;
        }

        await submitToServer({ latitude, longitude });
      },
      async (error) => {
        console.warn("Geolocation error:", error);
        alert("‚ö†Ô∏è Location not available. Please enable GPS for accurate complaint.");
        setIsSubmitting(false);
      },
      { enableHighAccuracy: true, timeout: 15000, maximumAge: 0 }
    );
  };

  // ‚úÖ yeh component ka asli return hai
  return (
    <div className="max-w-xl mx-auto p-6 bg-white rounded-2xl shadow-lg">
      <h2 className="text-2xl font-bold mb-4 text-center text-gray-800">
        üë©‚Äçüëß Women & Child Complaint
      </h2>

      {/* Speech Recognition Button */}
      <button
        onClick={handleSpeechToText}
        className={`w-full px-4 py-3 rounded-lg mb-4 font-semibold transition-all duration-300 ${
          recording 
            ? "bg-red-500 hover:bg-red-600 text-white animate-pulse" 
            : "bg-purple-600 hover:bg-purple-700 text-white"
        }`}
      >
        {recording ? (
          <div className="flex items-center justify-center space-x-2">
            <div className="w-4 h-4 bg-white rounded-full animate-pulse"></div>
            <span>‚èπ Stop Recording</span>
          </div>
        ) : (
          <div className="flex items-center justify-center space-x-2">
            <span>üéôÔ∏è Start Speaking</span>
          </div>
        )}
      </button>

      {/* Recording Status */}
      {recording && (
        <div className="mb-4 p-3 bg-red-50 border border-red-200 rounded-lg">
          <div className="flex items-center space-x-2 text-red-700">
            <div className="w-3 h-3 bg-red-500 rounded-full animate-pulse"></div>
            <span className="text-sm font-medium">Recording... Speak now!</span>
          </div>
          <p className="text-xs text-red-600 mt-1">
            Click the button again to stop recording
          </p>
        </div>
      )}

      {/* Complaint Text Area */}
      <div className="mb-4">
        <label className="block text-sm font-medium text-gray-700 mb-2">
          Complaint Details
        </label>
        <textarea
          placeholder="Type or speak your complaint here... (Click 'Start Speaking' to use voice input)"
          value={complaintText}
          onChange={(e) => setComplaintText(e.target.value)}
          className="w-full border rounded-lg p-3 h-32 focus:ring-2 focus:ring-blue-400 focus:border-transparent resize-none"
          disabled={recording}
        />
        {recording && (
          <p className="text-xs text-blue-600 mt-1">
            üí° You can still type while recording, or wait for speech to be processed
          </p>
        )}
      </div>

      {/* Submit Button */}
      <button
        onClick={handleSubmit}
        disabled={isSubmitting || !complaintText.trim()}
        className={`w-full ${
          isSubmitting || !complaintText.trim()
            ? "bg-gray-400 cursor-not-allowed"
            : "bg-green-600 hover:bg-green-700"
        } text-white px-4 py-3 rounded-lg font-semibold transition-colors`}
      >
        {isSubmitting ? (
          <div className="flex items-center justify-center space-x-2">
            <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
            <span>Submitting...</span>
          </div>
        ) : (
          "‚úÖ Submit Complaint"
        )}
      </button>

      {/* Success Message */}
      {token && (
        <div className="mt-4 p-4 border rounded-lg bg-green-50 border-green-200">
          <div className="flex items-center space-x-2 text-green-700">
            <span className="text-lg">‚úÖ</span>
            <span className="font-semibold">Complaint Submitted Successfully!</span>
          </div>
          <p className="text-sm text-green-600 mt-1">
            Your complaint has been registered. Token: {token}
          </p>
        </div>
      )}

      {/* Instructions */}
      <div className="mt-6 p-4 bg-blue-50 border border-blue-200 rounded-lg">
        <h3 className="text-sm font-medium text-blue-800 mb-2">üí° How to use:</h3>
        <ul className="text-xs text-blue-700 space-y-1">
          <li>‚Ä¢ Click "Start Speaking" and allow microphone access</li>
          <li>‚Ä¢ Speak your complaint clearly in Hindi or English</li>
          <li>‚Ä¢ Click "Stop Recording" when finished</li>
          <li>‚Ä¢ You can also type directly in the text area</li>
          <li>‚Ä¢ Click "Submit Complaint" when ready</li>
        </ul>
      </div>
    </div>
  );
}
