import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { ChevronRight, Mic, Zap, BarChart3, Shield, Users, CheckCircle, ArrowRight, Play, Star } from 'lucide-react';

const Home = () => {
  const navigate = useNavigate();
  const [scrollY, setScrollY] = useState(0);

  useEffect(() => {
    const handleScroll = () => setScrollY(window.scrollY);
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const FloatingElements = () => (
    <div className="absolute inset-0 overflow-hidden pointer-events-none">
      {/* Advanced Background Elements */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        {/* Mesh Gradient Background */}
        <div className="absolute inset-0 bg-gradient-to-br from-blue-300/30 via-purple-300/30 to-green-300/30 animate-pulse-slow"></div>
        
        {/* Animated Orbs with enhanced movements */}
        <div 
          className="absolute top-1/4 left-1/4 w-96 h-96 bg-gradient-to-r from-blue-400/20 to-purple-400/20 rounded-full blur-3xl animate-floating-orb animate-morph" 
          style={{ transform: `translate(${scrollY * 0.1}px, ${scrollY * 0.05}px)` }} 
        />
        <div 
          className="absolute top-3/4 right-1/4 w-80 h-80 bg-gradient-to-r from-green-400/20 to-blue-400/20 rounded-full blur-3xl animate-floating-orb-reverse animate-morph" 
          style={{ transform: `translate(${scrollY * -0.1}px, ${scrollY * 0.08}px)`, animationDelay: '2s' }} 
        />
        <div 
          className="absolute top-1/2 left-3/4 w-64 h-64 bg-gradient-to-r from-purple-400/20 to-pink-400/20 rounded-full blur-2xl animate-floating-orb animate-morph" 
          style={{ transform: `translate(${scrollY * 0.15}px, ${scrollY * -0.1}px)`, animationDelay: '1s' }} 
        />
        
        {/* Animated Grid Pattern */}
        <div className="absolute inset-0 opacity-5">
          <div className="grid grid-cols-20 gap-8 h-full w-full">
            {[...Array(200)].map((_, i) => (
              <div 
                key={i} 
                className="w-1 h-1 bg-gray-600 rounded-full animate-grid-sparkle" 
                style={{ animationDelay: `${Math.random() * 3}s` }} 
              />
            ))}
          </div>
        </div>
        
        {/* Enhanced Dynamic Particles */}
