import React, { useState, useRef } from "react";
import axios from "axios";

export default function WomenChildComplaint() {
  const [recording, setRecording] = useState(false);
  const [complaintText, setComplaintText] = useState("");
  const [token, setToken] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState(""); // New state for error messages
  const [status, setStatus] = useState("Ready to record"); // New state for status messages

  const recognitionRef = useRef(null);

  // 🎙️ Speech-to-text with robust browser support
  const handleSpeechToText = async () => {
    // Clear any previous errors
    setError("");

    if (recording) {
      // Stop recording
      if (recognitionRef.current) {
        try {
          recognitionRef.current.stop();
          console.log("🛑 Stopping speech recognition...");
        } catch (e) {
          console.warn("Recognition already stopped.");
        }
      }
      setRecording(false);
      return;
    }

    // Step 1: Check if we're in a secure context
    if (window.location.protocol !== 'https:' && window.location.hostname !== 'localhost') {
      alert("⚠️ Speech recognition requires HTTPS or localhost for security reasons.");
      return;
    }

    // Step 2: Request microphone permission first
    try {
      console.log("🎤 Requesting microphone permission...");
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      stream.getTracks().forEach(track => track.stop()); // Stop the stream after permission
      console.log("✅ Microphone permission granted");
    } catch (permissionError) {
      console.error("❌ Microphone permission denied:", permissionError);
      alert("❌ Microphone access denied! Please allow microphone access in your browser settings and refresh the page.");
      return;
    }

    // Step 3: Check for speech recognition support
    const SpeechRecognition = 
      window.SpeechRecognition || 
      window.webkitSpeechRecognition ||
      window.mozSpeechRecognition ||
      window.msSpeechRecognition;

    if (!SpeechRecognition) {
      alert("❌ Speech recognition is not supported in this browser.\n\nPlease use:\n• Google Chrome (recommended)\n• Microsoft Edge\n• Safari (on Mac)\n\nFirefox does not support speech recognition.");
      return;
    }

    // Step 4: Start speech recognition
    console.log("🎙️ Starting speech recognition...");

    try {
      const recognition = new SpeechRecognition();

      // Configure recognition settings - try multiple language options
      const languageOptions = ["en-US", "en-IN", "hi-IN", "en-GB"];
      recognition.lang = languageOptions[0]; // Start with English US
      recognition.continuous = false; // Set to false for better reliability
      recognition.interimResults = true;
      recognition.maxAlternatives = 1;

      // Event handlers
      recognition.onstart = () => {
        console.log("🚀 Speech recognition started");
        setRecording(true);
        setStatus("🎙️ Listening... Speak now!");
      };

      recognition.onresult = (event) => {
        console.log("🎯 Speech result received:", event);

        let finalTranscript = "";
        let interimTranscript = "";

        for (let i = event.resultIndex; i < event.results.length; i++) {
          const transcript = event.results[i][0].transcript;
          const confidence = event.results[i][0].confidence;

          console.log(`Result ${i}: "${transcript}" (confidence: ${confidence})`);

          if (event.results[i].isFinal) {
            finalTranscript += transcript + " ";
          } else {
            interimTranscript += transcript;
          }
        }

        // Update complaint text with final results
        if (finalTranscript) {
          setComplaintText(prev => {
            const newText = prev + " " + finalTranscript.trim();
            console.log("✅ Final transcript added:", finalTranscript);
            console.log("📝 Total text now:", newText);
            return newText;
          });
        }

        // Show interim results in console
        if (interimTranscript) {
          console.log("🎯 Interim transcript:", interimTranscript);
        }
      };

      recognition.onerror = (err) => {
        console.error("❌ Speech Recognition Error:", err);

        let errorMessage = "";
        switch (err.error) {
          case "no-speech":
            errorMessage = "No speech detected. Please try again and speak clearly into the microphone.";
            break;
          case "not-allowed":
            errorMessage = "Microphone permission denied. Please refresh the page and allow microphone access.";
            break;
          case "network":
            errorMessage = "Network error occurred. Please check your internet connection.";
            break;
          case "audio-capture":
            errorMessage = "No microphone found. Please connect a microphone and try again.";
            break;
          case "service-not-allowed":
            errorMessage = "Speech recognition service not allowed. Please try refreshing the page.";
            break;
          case "bad-grammar":
            errorMessage = "Speech recognition grammar error. Please try speaking more clearly.";
            break;
          case "language-not-supported":
            errorMessage = "Language not supported. Trying with English...";
            // Try switching to English if Hindi fails
            if (recognition.lang === "hi-IN") {
              recognition.lang = "en-US";
              console.log("🔄 Switching to English language");
              return; // Don't stop, just change language
            }
            break;
          default:
            errorMessage = `Speech recognition error: ${err.error}. Please try again.`;
        }

        console.error("Error details:", errorMessage);
        setError(errorMessage);
        setRecording(false);
      };

      recognition.onend = () => {
        console.log("🏁 Speech recognition ended");
        setRecording(false);
        setStatus("Ready to record");
      };

      recognition.onaudiostart = () => {
        console.log("🎵 Audio capture started");
        setStatus("🎵 Audio captured, speak now!");
      };

      recognition.onspeechstart = () => {
        console.log("🗣️ Speech detected");
        setStatus("🗣️ Speech detected! Keep speaking...");
      };

      recognition.onspeechend = () => {
        console.log("🤐 Speech ended");
        setStatus("🤐 Processing speech...");
      };

      // Start recognition
      recognition.start();
      recognitionRef.current = recognition;
      console.log("✅ Speech recognition started successfully");

    } catch (error) {
      console.error("❌ Failed to start speech recognition:", error);
      alert(`Failed to start speech recognition: ${error.message}\n\nPlease try refreshing the page.`);
      setRecording(false);
    }
  };

  // 🔄 Alternative: Manual text input fallback
  const handleManualInput = () => {
    const manualText = prompt("Please type your complaint manually:");
    if (manualText && manualText.trim()) {
      setComplaintText(manualText.trim());
      console.log("📝 Manual text added:", manualText);
    }
  };

  // 📤 Submit Complaint
  const handleSubmit = async () => {
    if (!complaintText.trim()) {
      alert("Please speak or type your complaint.");
      return;
    }

    setIsSubmitting(true);

    const submitToServer = async (coords) => {
      try {
        const formData = new FormData();
        formData.append("text", complaintText);
        formData.append("category", "women-child");

        if (coords) {
          formData.append("latitude", coords.latitude);
          formData.append("longitude", coords.longitude);
        }

        // Debug logging
        console.log("🔍 Submitting complaint with text:", complaintText);
        console.log("🔍 FormData contents:");
        for (let [key, value] of formData.entries()) {
          console.log(`  ${key}: ${value}`);
        }

        const res = await axios.post(
          "http://localhost:5000/api/complaint/women-child", // ✅ endpoint
          formData,
          { headers: { "Content-Type": "multipart/form-data" } }
        );

        console.log("🔍 Backend response:", res.data);

        if (res.data?.success) {
          setToken(res.data.token);
          setComplaintText("");
          alert(
            `Complaint submitted successfully${coords ? " with location" : ""}!`
          );
        } else {
          alert(res.data?.message || "Failed to submit complaint.");
        }
      } catch (err) {
        console.error("❌ Error submitting complaint:", err);
        alert("Error submitting complaint.");
      } finally {
        setIsSubmitting(false);
      }
    };

    // 📍 Location capture
    if (!navigator.geolocation) {
      await submitToServer(null);
      return;
    }

    navigator.geolocation.getCurrentPosition(
      async (position) => {
        const { latitude, longitude } = position.coords;

        if (!latitude || !longitude || latitude === 0 || longitude === 0) {
          alert("Unable to fetch accurate location. Please try again outdoors.");
          setIsSubmitting(false);
          return;
        }

        await submitToServer({ latitude, longitude });
      },
      async (error) => {
        console.warn("Geolocation error:", error);
        alert("⚠️ Location not available. Please enable GPS for accurate complaint.");
        setIsSubmitting(false);
      },
      { enableHighAccuracy: true, timeout: 15000, maximumAge: 0 }
    );
  };

  // 🗑️ Clear complaint text
  const clearComplaint = () => {
    setComplaintText("");
    setToken(null);
    console.log("🧹 Complaint text cleared");
  };

  // ✅ yeh component ka asli return hai
  return (
    <div className="max-w-xl mx-auto p-6 bg-white rounded-2xl shadow-lg">
      <h2 className="text-2xl font-bold mb-4 text-center text-gray-800">
        👩‍👧 Women & Child Complaint
      </h2>

      {/* Speech Recognition Button */}
      <button
        onClick={handleSpeechToText}
        className={`w-full px-4 py-3 rounded-lg mb-4 font-semibold transition-all duration-300 ${
          recording 
            ? "bg-red-500 hover:bg-red-600 text-white animate-pulse" 
            : "bg-purple-600 hover:bg-purple-700 text-white"
        }`}
      >
        {recording ? (
          <div className="flex items-center justify-center space-x-2">
            <div className="w-4 h-4 bg-white rounded-full animate-pulse"></div>
            <span>⏹ Stop Recording</span>
          </div>
        ) : (
          <div className="flex items-center justify-center space-x-2">
            <span>🎙️ Start Speaking</span>
          </div>
        )}
      </button>

      {/* Recording Status */}
      {recording && (
        <div className="mb-4 p-3 bg-red-50 border border-red-200 rounded-lg">
          <div className="flex items-center space-x-2 text-red-700">
            <div className="w-3 h-3 bg-red-500 rounded-full animate-pulse"></div>
            <span className="text-sm font-medium">Recording... Speak now!</span>
          </div>
          <p className="text-xs text-red-600 mt-1">
            Click the button again to stop recording
          </p>
        </div>
      )}

      {/* Complaint Text Area */}
      <div className="mb-4">
        <label className="block text-sm font-medium text-gray-700 mb-2">
          Complaint Details
        </label>
        <textarea
          placeholder="Type or speak your complaint here... (Click 'Start Speaking' to use voice input)"
          value={complaintText}
          onChange={(e) => setComplaintText(e.target.value)}
          className="w-full border rounded-lg p-3 h-32 focus:ring-2 focus:ring-blue-400 focus:border-transparent resize-none"
          disabled={recording}
        />
        {recording && (
          <p className="text-xs text-blue-600 mt-1">
            💡 You can still type while recording, or wait for speech to be processed
          </p>
        )}
      </div>

      {/* Submit Button */}
      <div className="flex space-x-3">
        <button
          onClick={handleSubmit}
          disabled={isSubmitting || !complaintText.trim()}
          className={`flex-1 ${
            isSubmitting || !complaintText.trim()
              ? "bg-gray-400 cursor-not-allowed"
              : "bg-green-600 hover:bg-green-700"
          } text-white px-4 py-3 rounded-lg font-semibold transition-colors`}
        >
          {isSubmitting ? (
            <div className="flex items-center justify-center space-x-2">
              <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
              <span>Submitting...</span>
            </div>
          ) : (
            "✅ Submit Complaint"
          )}
        </button>

        {complaintText.trim() && (
          <button
            onClick={clearComplaint}
            className="px-4 py-3 bg-gray-500 hover:bg-gray-600 text-white rounded-lg font-semibold transition-colors"
            title="Clear complaint text"
          >
            🗑️ Clear
          </button>
        )}
      </div>

      {/* Success Message */}
      {token && (
        <div className="mt-4 p-4 border rounded-lg bg-green-50 border-green-200">
          <div className="flex items-center space-x-2 text-green-700">
            <span className="text-lg">✅</span>
            <span className="font-semibold">Complaint Submitted Successfully!</span>
          </div>
          <p className="text-sm text-green-600 mt-1">
            Your complaint has been registered. Token: {token}
          </p>
        </div>
      )}

      {/* Instructions */}
      <div className="mt-6 p-4 bg-blue-50 border border-blue-200 rounded-lg">
        <h3 className="text-sm font-medium text-blue-800 mb-2">💡 How to use:</h3>
        <ul className="text-xs text-blue-700 space-y-1">
          <li>• Click "Start Speaking" and allow microphone access</li>
          <li>• Speak your complaint clearly in Hindi or English</li>
          <li>• Click "Stop Recording" when finished</li>
          <li>• You can also type directly in the text area</li>
          <li>• Click "Submit Complaint" when ready</li>
        </ul>
      </div>

      {/* Debug Information */}
      <div className="mt-4 p-3 bg-gray-50 border border-gray-200 rounded-lg">
        <h3 className="text-xs font-medium text-gray-700 mb-2">🔧 Debug Info:</h3>
        <div className="text-xs text-gray-600 space-y-1">
          <div>Browser: {navigator.userAgent.includes('Chrome') ? '✅ Chrome' : navigator.userAgent.includes('Edge') ? '✅ Edge' : navigator.userAgent.includes('Safari') ? '✅ Safari' : '❌ Unknown'}</div>
          <div>Speech Recognition: {window.SpeechRecognition || window.webkitSpeechRecognition ? '✅ Supported' : '❌ Not Supported'}</div>
          <div>Microphone Permission: {navigator.permissions ? '✅ Checkable' : '❌ Not Checkable'}</div>
          <div>HTTPS/Localhost: {window.location.protocol === 'https:' || window.location.hostname === 'localhost' ? '✅ Secure Context' : '❌ Not Secure'}</div>
        </div>
      </div>
    </div>
  );
}
