import React, { useState, useRef } from "react";
import axios from "axios";

export default function WomenChildComplaint() {
  const [recording, setRecording] = useState(false);
  const [complaintText, setComplaintText] = useState("");
  const [token, setToken] = useState(null);

  const recognitionRef = useRef(null); // 👈 yahan store hoga recognition object

  // 🗣️ Speech-to-Text
  const handleSpeechToText = () => {
    const SpeechRecognition =
      window.SpeechRecognition || window.webkitSpeechRecognition;

    if (!SpeechRecognition) {
      alert("❌ Speech recognition not supported in this browser. Use Chrome.");
      return;
    }

    // 🔴 Agar already recording hai → stop kar do
    if (recording && recognitionRef.current) {
      recognitionRef.current.stop();
      setRecording(false);
      return;
    }

    // 🎤 Naya recognition object banao
    const recognition = new SpeechRecognition();
    recognition.lang = "hi-IN"; // Hindi
    recognition.continuous = true;
    recognition.interimResults = true;

    recognition.onresult = (event) => {
      let transcript = "";
      for (let i = 0; i < event.results.length; ++i) {
        transcript += event.results[i][0].transcript + " ";
      }
      setComplaintText(transcript.trim());
    };

    recognition.onerror = (err) => {
      console.error("Speech Recognition Error:", err);
      if (err.error === "no-speech") {
        alert("❌ Mic me koi awaaz detect nahi hui. Dubara try karo.");
      } else {
        alert("Speech recognition error: " + err.error);
      }
      setRecording(false);
    };

    recognition.onend = () => {
      setRecording(false);
    };

    recognition.start();
    recognitionRef.current = recognition; // 👈 yahan save kar diya
    setRecording(true);
  };

  // 📤 Submit Complaint
  const handleSubmit = async () => {
    if (!complaintText) {
      alert("Please speak or type your complaint.");
      return;
    }

    try {
      const formData = new FormData();
      formData.append("category", "women-child");
      formData.append("text", complaintText);

      const res = await axios.post("http://localhost:5000/api/complaint", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      if (res.data.success) {
        setToken(res.data.token);
        setComplaintText("");
        alert("✅ Complaint submitted successfully!");
      }
    } catch (err) {
      console.error(err);
      alert("❌ Error submitting complaint");
    }
  };

  return (
    <div className="max-w-xl mx-auto p-6 bg-white rounded-2xl shadow-lg">
      <h2 className="text-2xl font-bold mb-4 text-center text-gray-800">
        👩‍👧 Women & Child Complaint
      </h2>

      {/* 🗣️ Speak Button */}
      <button
        onClick={handleSpeechToText}
        className={`w-full px-4 py-2 rounded-lg mb-3 font-semibold ${
          recording ? "bg-red-500" : "bg-purple-600"
        } text-white`}
      >
        {recording ? "⏹ Stop Speaking" : "🗣 Speak Complaint"}
      </button>

      {/* 📝 Complaint Box */}
      <textarea
        placeholder="Type or speak your complaint here..."
        value={complaintText}
        onChange={(e) => setComplaintText(e.target.value)}
        className="w-full border rounded-lg p-3 mb-3 h-32 focus:ring-2 focus:ring-blue-400"
      />

      {/* 📤 Submit */}
      <button
        onClick={handleSubmit}
        className="w-full bg-green-600 text-white px-4 py-2 rounded-lg font-semibold"
      >
        ✅ Submit Complaint
      </button>

      {/* 🎫 Token Display */}
      {token && (
        <div className="mt-4 p-4 border rounded-lg bg-gray-100 shadow-sm">
          <p className="font-semibold text-green-700">✅ Complaint Submitted!</p>
          <p className="text-gray-700">
            Your Token Number:{" "}
            <span className="font-mono text-lg text-black">{token}</span>
          </p>
        </div>
      )}
    </div>
  );
}
