import React, { useState } from "react";
import axios from "axios";

export default function WomenChildComplaint() {
  const [name, setName] = useState("");
  const [complaintText, setComplaintText] = useState("");
  const [token, setToken] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  // 📤 Submit Complaint
  const handleSubmit = async () => {
    if (!name.trim()) {
      alert("Please enter your name.");
      return;
    }
    if (!complaintText.trim()) {
      alert("Please type your complaint.");
      return;
    }

    setIsSubmitting(true);

    const submitToServer = async (coords) => {
      try {
        const formData = new FormData();
        formData.append("name", name);
        formData.append("text", complaintText);
        formData.append("category", "Complaint");

        if (coords) {
          formData.append("latitude", coords.latitude);
          formData.append("longitude", coords.longitude);
        }

        const res = await axios.post(
          "http://localhost:5000/api/complaint/women-child", // ✅ backend endpoint
          formData,
          { headers: { "Content-Type": "multipart/form-data" } }
        );

        if (res.data?.success) {
          setToken(res.data.token);
          setComplaintText("");
          setName("");
          alert(
            `Complaint submitted successfully${coords ? " with location" : ""}!`
          );
        } else {
          alert(res.data?.message || "Failed to submit complaint.");
        }
      } catch (err) {
        console.error(err);
        alert("Error submitting complaint.");
      } finally {
        setIsSubmitting(false);
      }
    };

    // 📍 Location capture
    if (!navigator.geolocation) {
      await submitToServer(null);
      return;
    }

    navigator.geolocation.getCurrentPosition(
      async (position) => {
        const { latitude, longitude } = position.coords;

        if (!latitude || !longitude || latitude === 0 || longitude === 0) {
          alert("Unable to fetch accurate location. Please try again outdoors.");
          setIsSubmitting(false);
          return;
        }

        await submitToServer({ latitude, longitude });
      },
      async (error) => {
        console.warn("Geolocation error:", error);
        alert("⚠ Location not available. Please enable GPS for accurate complaint.");
        setIsSubmitting(false);
      },
      { enableHighAccuracy: true, timeout: 15000, maximumAge: 0 }
    );
  };

  return (
    <div className="max-w-xl mx-auto p-6 bg-white rounded-2xl shadow-lg">
      <h2 className="text-2xl font-bold mb-4 text-center text-gray-800">
        Complaint Form
      </h2>

      {/* Name Field */}
      <input
        type="text"
        placeholder="Your Name"
        value={name}
        onChange={(e) => setName(e.target.value)}
        className="w-full border rounded-lg p-3 mb-3 focus:ring-2 focus:ring-blue-400"
      />

      <textarea
        placeholder="Type your complaint here..."
        value={complaintText}
        onChange={(e) => setComplaintText(e.target.value)}
        className="w-full border rounded-lg p-3 mb-3 h-32 focus:ring-2 focus:ring-blue-400"
      />

      <button
        onClick={handleSubmit}
        disabled={isSubmitting}
        className={`w-full ${
          isSubmitting ? "bg-gray-400" : "bg-green-600"
        } text-white px-4 py-2 rounded-lg font-semibold`}
      >
        {isSubmitting ? "Submitting..." : "✅ Submit Complaint"}
      </button>

      {token && (
        <div className="mt-4 p-4 border rounded-lg bg-gray-100 shadow-sm">
          <p className="font-semibold text-green-700">✅ Complaint Submitted!</p>
          <p className="text-gray-700">
            Your Token Number:{" "}
            <span className="font-mono text-lg text-black">{token}</span>
          </p>
        </div>
      )}

      <p className="text-xs text-gray-500 mt-3">
        Note: Location is captured automatically on submit (works on HTTPS or localhost).
      </p>
    </div>
  );
}
