// src/components/NavBar.jsx
import React from 'react';
import { Link } from 'react-router-dom';

const NavBar = () => {
  return (
    <div>
      {/* Government Header Bar */}
      <div className="bg-gradient-to-r from-[#FF8000] via-[#FF6F00] to-[#FF8000] text-white px-6 py-4 flex items-center justify-between shadow-md border-b-2 border-[#cc5c00]"></div>
      
     <nav className="bg-gradient-to-r from-[#FF8000] via-[#FF6F00] to-[#FF8000] text-white px-6 py-4 flex items-center justify-between shadow-md border-b-2 border-[#cc5c00]">


        
        {/* Left section: JANTA VOICE + image */}
        <div className="flex items-center space-x-4">
          <div className="relative">
            <img 
              src="/images/preamble.jpg" 
              className="h-12 w-12 object-contain rounded-lg border-2 border-white/30 shadow-md bg-white/10 p-1" 
            />
            <div className="absolute -top-1 -right-1 w-4 h-4 bg-orange-500 rounded-full flex items-center justify-center">
              <span className="text-white text-xs font-bold">🇮🇳</span>
            </div>
          </div>
          <div>
            <h1 className="text-2xl md:text-3xl font-bold tracking-wide text-white drop-shadow-lg">
              जनता VOICE
            </h1>
            <p className="text-xs text-blue-200 font-medium mt-0.5">
              Government of India | भारत सरकार
            </p>
          </div>
        </div>

        {/* Right section: enhanced navigation links */}
        <div className="hidden md:flex items-center space-x-1">
          <Link 
            to="/schemes" 
            className="px-4 py-2 rounded-lg text-sm font-medium text-white/90 hover:text-white hover:bg-white/10 transition-all duration-200 border border-transparent hover:border-white/20 backdrop-blur-sm"
          >
            <span className="flex items-center space-x-2">
              <span className="text-base">📋</span>
              <span>योजनाएं | Schemes</span>
            </span>
          </Link>
          
          <Link 
            to="/acts" 
            className="px-4 py-2 rounded-lg text-sm font-medium text-white/90 hover:text-white hover:bg-white/10 transition-all duration-200 border border-transparent hover:border-white/20 backdrop-blur-sm"
          >
            <span className="flex items-center space-x-2">
              <span className="text-base">⚖️</span>
              <span>अधिनियम | Acts</span>
            </span>
          </Link>
          
          <Link 
            to="/policies" 
            className="px-4 py-2 rounded-lg text-sm font-medium text-white/90 hover:text-white hover:bg-white/10 transition-all duration-200 border border-transparent hover:border-white/20 backdrop-blur-sm"
          >
            <span className="flex items-center space-x-2">
              <span className="text-base">📜</span>
              <span>नीतियां | Policies</span>
            </span>
          </Link>
          
          <Link 
            to="/help" 
            className="px-4 py-2 rounded-lg text-sm font-medium text-white/90 hover:text-white hover:bg-white/10 transition-all duration-200 border border-transparent hover:border-white/20 backdrop-blur-sm"
          >
            <span className="flex items-center space-x-2">
              <span className="text-base">❓</span>
              <span>सहायता | Help</span>
            </span>
          </Link>

          {/* Quick Access Button */}
          <div className="ml-4 pl-4 border-l border-white/20">
            <Link 
              to="/trackstatus"
              className="bg-green-600 hover:bg-green-700 px-4 py-2 rounded-lg text-sm font-semibold text-white transition-all duration-200 shadow-md hover:shadow-lg transform hover:scale-105 border border-green-500"
            >
              <span className="flex items-center space-x-2">
                <span className="text-base">🔍</span>
                <span>Track Status</span>
              </span>
            </Link>
          </div>
        </div>

        {/* Mobile menu button */}
        <div className="md:hidden">
          <button className="text-white p-2 rounded-lg hover:bg-white/10 transition-colors">
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
            </svg>
          </button>
        </div>
      </nav>

      {/* Government Info Banner */}
      <div className="bg-blue-50 border-b border-blue-200 px-6 py-2">
        <div className="flex items-center justify-between text-sm">
          <div className="flex items-center space-x-4 text-gray-700">
            <span className="flex items-center space-x-1">
              <span className="text-green-600">🟢</span>
              <span className="font-medium">Portal Status: Active</span>
            </span>
            <span className="hidden md:block text-gray-500">|</span>
            <span className="hidden md:flex items-center space-x-1">
              <span className="text-blue-600">📞</span>
              <span>24/7 Support Available</span>
            </span>
          </div>
          <div className="flex items-center space-x-4 text-gray-600 text-xs">
            <span>Last Updated: {new Date().toLocaleDateString('en-IN')}</span>
            <Link to="/accessibility" className="hover:text-blue-600 transition-colors">
              Accessibility
            </Link>
            <Link to="/sitemap" className="hover:text-blue-600 transition-colors">
              Sitemap
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default NavBar;
