import React, { useState, useRef } from "react";
import axios from "axios";

export default function EmergencyComplaint() {
  const [recording, setRecording] = useState(false);
  const [complaintText, setComplaintText] = useState("");
  const [token, setToken] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState(""); 
  const [status, setStatus] = useState("Ready to record"); 

  const recognitionRef = useRef(null);

  // üéôÔ∏è Speech-to-text with robust browser support
  const handleSpeechToText = async () => {
    setError("");

    if (recording) {
      if (recognitionRef.current) {
        try {
          recognitionRef.current.stop();
          console.log("üõë Stopping speech recognition...");
        } catch (e) {
          console.warn("Recognition already stopped.");
        }
      }
      setRecording(false);
      return;
    }

    if (window.location.protocol !== 'https:' && window.location.hostname !== 'localhost') {
      alert("‚ö†Ô∏è Speech recognition requires HTTPS or localhost for security reasons.");
      return;
    }

    try {
      console.log("üé§ Requesting microphone permission...");
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      stream.getTracks().forEach(track => track.stop());
      console.log("‚úÖ Microphone permission granted");
    } catch (permissionError) {
      console.error("‚ùå Microphone permission denied:", permissionError);
      alert("‚ùå Microphone access denied! Please allow microphone access in your browser settings and refresh the page.");
      return;
    }

    const SpeechRecognition =
      window.SpeechRecognition ||
      window.webkitSpeechRecognition ||
      window.mozSpeechRecognition ||
      window.msSpeechRecognition;

    if (!SpeechRecognition) {
      alert("‚ùå Speech recognition is not supported in this browser.\n\nPlease use:\n‚Ä¢ Google Chrome (recommended)\n‚Ä¢ Microsoft Edge\n‚Ä¢ Safari (on Mac)\n\nFirefox does not support speech recognition.");
      return;
    }

    console.log("üéôÔ∏è Starting speech recognition...");
    try {
      const recognition = new SpeechRecognition();

      recognition.lang = "en-US";
      recognition.continuous = false;
      recognition.interimResults = true;
      recognition.maxAlternatives = 1;

      recognition.onstart = () => {
        console.log("üöÄ Speech recognition started");
        setRecording(true);
        setStatus("üéôÔ∏è Listening... Speak now!");
      };

      recognition.onresult = (event) => {
        console.log("üéØ Speech result received:", event);
        let finalTranscript = "";
        let interimTranscript = "";

        for (let i = event.resultIndex; i < event.results.length; i++) {
          const transcript = event.results[i][0].transcript;
          const confidence = event.results[i][0].confidence;
          console.log(`Result ${i}: "${transcript}" (confidence: ${confidence})`);
          if (event.results[i].isFinal) {
            finalTranscript += transcript + " ";
          } else {
            interimTranscript += transcript;
          }
        }

        if (finalTranscript) {
          setComplaintText(prev => {
            const newText = prev + " " + finalTranscript.trim();
            console.log("‚úÖ Final transcript added:", finalTranscript);
            console.log("üìù Total text now:", newText);
            return newText;
          });
        }
        if (interimTranscript) {
          console.log("üéØ Interim transcript:", interimTranscript);
        }
      };

      recognition.onerror = (err) => {
        console.error("‚ùå Speech Recognition Error:", err);
        let errorMessage = "";
        switch (err.error) {
          case "no-speech":
            errorMessage = "No speech detected. Please try again and speak clearly into the microphone.";
            break;
          case "not-allowed":
            errorMessage = "Microphone permission denied. Please refresh the page and allow microphone access.";
            break;
          case "network":
            errorMessage = "Network error occurred. Please check your internet connection.";
            break;
          case "audio-capture":
            errorMessage = "No microphone found. Please connect a microphone and try again.";
            break;
          case "service-not-allowed":
            errorMessage = "Speech recognition service not allowed. Please try refreshing the page.";
            break;
          default:
            errorMessage = `Speech recognition error: ${err.error}. Please try again.`;
        }
        console.error("Error details:", errorMessage);
        setError(errorMessage);
        setRecording(false);
      };

      recognition.onend = () => {
        console.log("üèÅ Speech recognition ended");
        setRecording(false);
        setStatus("Ready to record");
      };

      recognition.onaudiostart = () => {
        console.log("üéµ Audio capture started");
        setStatus("üéµ Audio captured, speak now!");
      };

      recognition.onspeechstart = () => {
        console.log("üó£Ô∏è Speech detected");
        setStatus("üó£Ô∏è Speech detected! Keep speaking...");
      };

      recognition.onspeechend = () => {
        console.log("ü§ê Speech ended");
        setStatus("ü§ê Processing speech...");
      };

      recognition.start();
      recognitionRef.current = recognition;
      console.log("‚úÖ Speech recognition started successfully");
    } catch (error) {
      console.error("‚ùå Failed to start speech recognition:", error);
      alert(`Failed to start speech recognition: ${error.message}\n\nPlease try refreshing the page.`);
      setRecording(false);
    }
  };

  const handleManualInput = () => {
    const manualText = prompt("Please type your complaint manually:");
    if (manualText && manualText.trim()) {
      setComplaintText(manualText.trim());
      console.log("üìù Manual text added:", manualText);
    }
  };

  const handleSubmit = async () => {
    if (!complaintText.trim()) {
      alert("Please speak or type your complaint.");
      return;
    }

    setIsSubmitting(true);

    const submitToServer = async (coords) => {
      try {
        const formData = new FormData();
        formData.append("text", complaintText);
        formData.append("category", "emergency"); // changed from women-child

        if (coords) {
          formData.append("latitude", coords.latitude);
          formData.append("longitude", coords.longitude);
        }

        console.log("üîç Submitting complaint with text:", complaintText);
        for (let [key, value] of formData.entries()) {
          console.log(`  ${key}: ${value}`);
        }

        const res = await axios.post(
          "http://localhost:5000/api/complaint/emergency", // changed endpoint
          formData,
          { headers: { "Content-Type": "multipart/form-data" } }
        );

        console.log("üîç Backend response:", res.data);

        if (res.data?.success) {
          setToken(res.data.token);
          setComplaintText("");
          alert(
            `Complaint submitted successfully${coords ? " with location" : ""}!`
          );
        } else {
          alert(res.data?.message || "Failed to submit complaint.");
        }
      } catch (err) {
        console.error("‚ùå Error submitting complaint:", err);
        alert("Error submitting complaint.");
      } finally {
        setIsSubmitting(false);
      }
    };

    if (!navigator.geolocation) {
      await submitToServer(null);
      return;
    }

    navigator.geolocation.getCurrentPosition(
      async (position) => {
        const { latitude, longitude } = position.coords;
        if (!latitude || !longitude || latitude === 0 || longitude === 0) {
          alert("Unable to fetch accurate location. Please try again outdoors.");
          setIsSubmitting(false);
          return;
        }
        await submitToServer({ latitude, longitude });
      },
      async (error) => {
        console.warn("Geolocation error:", error);
        alert("‚ö†Ô∏è Location not available. Please enable GPS for accurate complaint.");
        setIsSubmitting(false);
      },
      { enableHighAccuracy: true, timeout: 15000, maximumAge: 0 }
    );
  };

  const clearComplaint = () => {
    setComplaintText("");
    setToken(null);
    console.log("üßπ Complaint text cleared");
  };

  return (
    <div className="max-w-xl mx-auto p-6 bg-white rounded-2xl shadow-lg">
      <h2 className="text-2xl font-bold mb-4 text-center text-gray-800">
        üö® Emergency Complaint
      </h2>

      {/* Complaint Textarea */}
      <textarea
        className="w-full border rounded-lg p-3 mb-4"
        rows="4"
        placeholder="Your complaint will appear here..."
        value={complaintText}
        onChange={(e) => setComplaintText(e.target.value)}
      />

      {/* Status */}
      <p className="text-sm text-gray-600 mb-2">{status}</p>
      {error && <p className="text-sm text-red-500 mb-2">‚ö†Ô∏è {error}</p>}

      {/* Action Buttons */}
      <div className="flex flex-wrap gap-2 mb-4">
        <button
          onClick={handleSpeechToText}
          className={`px-4 py-2 rounded-lg text-white ${
            recording ? "bg-red-600" : "bg-blue-600"
          }`}
        >
          {recording ? "üõë Stop Recording" : "üé§ Start Recording"}
        </button>

        <button
          onClick={handleManualInput}
          className="px-4 py-2 rounded-lg bg-gray-600 text-white"
        >
          ‚úçÔ∏è Manual Input
        </button>

        <button
          onClick={handleSubmit}
          disabled={isSubmitting}
          className="px-4 py-2 rounded-lg bg-green-600 text-white disabled:opacity-50"
        >
          {isSubmitting ? "‚è≥ Submitting..." : "üì® Submit Complaint"}
        </button>

        <button
          onClick={clearComplaint}
          className="px-4 py-2 rounded-lg bg-yellow-500 text-white"
        >
          üßπ Clear
        </button>
      </div>

      {/* Complaint Token */}
      {token && (
        <div className="bg-green-100 text-green-800 p-3 rounded-lg text-sm">
          ‚úÖ Complaint submitted! Token: <b>{token}</b>
        </div>
      )}
    </div>
  );
}
