// export default AdminDashboard;

import React, { useEffect, useState } from 'react';
import { Search, Eye, Download, RefreshCw, Calendar, Clock, User, MapPin, Building, AlertTriangle } from 'lucide-react';
import axios from 'axios';
import CasesByDepartment from './CasesByDepartment'; // Assumed component
import CasesByCity from './CasesByCity'; // Assumed component
import MonthlyTrend from './MonthlyTrend'; // Assumed component
import TopCities from './TopCities'; // Assumed component

// Helper function to safely convert a value to a number or return null
const toNumberOrNull = (v) => {
  const n = Number(v);
  return Number.isFinite(n) ? n : null;
};

// Helper function to normalize and handle swapped latitude/longitude coordinates
const normalizeLatLng = (lat, lng) => {
  const la = toNumberOrNull(lat);
  const lo = toNumberOrNull(lng);
  if (la === null || lo === null) return { lat: null, lng: null, swapped: false };
  if (Math.abs(la) > 90 && Math.abs(lo) <= 90) return { lat: lo, lng: la, swapped: true };
  return { lat: la, lng: lo, swapped: false };
};

// Component for embedding a Google Map
const GoogleMapEmbed = ({ latitude, longitude }) => {
  if (!latitude || !longitude) {
    return (
      <div className="w-full h-64 bg-gray-100 rounded-lg flex items-center justify-center">
        <p className="text-gray-500">No location data available</p>
      </div>
    );
  }
  const mapUrl = `http://maps.google.com/maps?q=${latitude},${longitude}&z=15&output=embed`;

  return (
    <div className="w-full h-64 rounded-lg overflow-hidden border border-gray-300">
      <iframe
        width="100%"
        height="100%"
        frameBorder="0"
        src={mapUrl}
        allowFullScreen
        title={`Map for location ${latitude},${longitude}`}
      ></iframe>
    </div>
  );
};

const AdminDashboard = () => {
  const [complaints, setComplaints] = useState([]);
  const [filteredComplaints, setFilteredComplaints] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");
  const [statusFilter, setStatusFilter] = useState("All");
  const [departmentFilter, setDepartmentFilter] = useState("All");
  const [dateFilter, setDateFilter] = useState("All");
  const [selectedComplaint, setSelectedComplaint] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [departments, setDepartments] = useState([]);
  const [revAddress, setRevAddress] = useState(null);
  const [addrLoading, setAddrLoading] = useState(false);
  const [addrError, setAddrError] = useState(null);

  const fetchComplaints = async () => {
    setLoading(true);
    try {
      const res = await axios.get("/api/admin/complaints");
      const data = res.data;
      if (data.success) {
        setComplaints(data.complaints);
      } else {
        console.error(data.message);
      }
    } catch (err) {
      console.error("Error fetching complaints:", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchComplaints();
    const intervalId = setInterval(fetchComplaints, 10000);
    return () => clearInterval(intervalId);
  }, []);

  useEffect(() => {
    const uniqueDepartments = [...new Set(complaints.map(c => c.department).filter(Boolean))];
    setDepartments(uniqueDepartments);

    let filtered = complaints;

    if (searchTerm) {
      filtered = filtered.filter(c =>
        c.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        c.location?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        c.department?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        c.description?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        c.id?.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    if (statusFilter !== "All") {
      filtered = filtered.filter(c => c.status === statusFilter);
    }

    if (departmentFilter !== "All") {
      filtered = filtered.filter(c => c.department === departmentFilter);
    }

    if (dateFilter !== "All") {
      const now = new Date();
      filtered = filtered.filter(c => {
        const complaintDate = new Date(c.timestamp);
        switch (dateFilter) {
          case "Today":
            return complaintDate.toDateString() === now.toDateString();
          case "This Week":
            const weekAgo = new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000);
            return complaintDate >= weekAgo;
          case "This Month":
            return complaintDate.getMonth() === now.getMonth() &&
              complaintDate.getFullYear() === now.getFullYear();
          default:
            return true;
        }
      });
    }

    setFilteredComplaints(filtered);
  }, [complaints, searchTerm, statusFilter, departmentFilter, dateFilter]);

  const handleStatusChange = async (id, newStatus) => {
    try {
      const res = await axios.post("/api/admin/update_status", { id, status: newStatus });
      if (res.data.success) {
        setComplaints(prev =>
          prev.map(c => (c.id === id ? { ...c, status: newStatus } : c))
        );
      } else {
        alert(res.data.message);
      }
    } catch (err) {
      console.error("Error updating status:", err);
    }
  };

  const getStatusColor = status => {
    switch (status) {
      case "Resolved": return "bg-green-100 text-green-800 border-green-200";
      case "Processing": return "bg-yellow-100 text-yellow-800 border-yellow-200";
      case "Pending": return "bg-red-100 text-red-800 border-red-200";
      default: return "bg-gray-100 text-gray-800 border-gray-200";
    }
  };

  const getUrgencyColor = urgency => {
    switch (urgency?.toLowerCase()) {
      case "high": return "bg-red-500 text-white";
      case "medium": return "bg-yellow-500 text-white";
      case "low": return "bg-green-500 text-white";
      default: return "bg-blue-500 text-white";
    }
  };

  const getPriorityIcon = urgency => {
    if (urgency?.toLowerCase() === "high") {
      return <AlertTriangle className="w-4 h-4" />;
    }
    return null;
  };

  const stats = {
    total: complaints.length,
    pending: complaints.filter(c => c.status === "Pending").length,
    processing: complaints.filter(c => c.status === "Processing").length,
    resolved: complaints.filter(c => c.status === "Resolved").length,
    today: complaints.filter(c => new Date(c.timestamp).toDateString() === new Date().toDateString()).length,
    thisWeek: complaints.filter(c => new Date(c.timestamp) >= new Date(Date.now() - 7 * 24 * 60 * 60 * 1000)).length
  };

  const exportToCSV = () => {
    const headers = ["ID", "Name", "Location", "Department", "Status", "Urgency", "Description", "Timestamp", "Token", "Latitude", "Longitude"];
    const csvContent = [
      headers.join(","),
      ...filteredComplaints.map(c => [
        c.id,
        `"${c.name?.replace(/"/g, '""') || ''}"`,
        `"${c.location?.replace(/"/g, '""') || ''}"`,
        c.department || "Unknown",
        c.status,
        c.urgency || "Normal",
        `"${c.description?.replace(/"/g, '""') || ''}"`,
        new Date(c.timestamp).toLocaleString(),
        c.token,
        c.latitude || "",
        c.longitude || ""
      ].join(","))
    ].join("\n");

    const blob = new Blob([csvContent], { type: "text/csv;charset=utf-8;" });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = `complaints_${new Date().toISOString().split('T')[0]}.csv`;
    a.click();
    window.URL.revokeObjectURL(url);
  };

  const viewComplaint = (complaint) => {
    setSelectedComplaint(complaint);
    setShowModal(true);
  };

  const selectedLatLng = React.useMemo(() =>
    selectedComplaint
      ? normalizeLatLng(selectedComplaint.latitude, selectedComplaint.longitude)
      : { lat: null, lng: null, swapped: false },
    [selectedComplaint]
  );

  useEffect(() => {
    if (!showModal || !selectedComplaint) return;

    const { lat, lng } = selectedLatLng;
    if (lat === null || lng === null) {
      setRevAddress(null);
      setAddrError(null);
      setAddrLoading(false);
      return;
    }

    setAddrLoading(true);
    setAddrError(null);
    setRevAddress(null);

    // Use Nominatim for reverse geocoding
    fetch(`https://nominatim.openstreetmap.org/reverse?format=json&lat=${lat}&lon=${lng}&zoom=18`)
      .then(r => r.ok ? r.json() : Promise.reject(new Error(`HTTP ${r.status}`)))
      .then(j => setRevAddress(j?.display_name || "Address not found."))
      .catch(() => setAddrError("Address lookup failed."))
      .finally(() => setAddrLoading(false));
  }, [showModal, selectedComplaint, selectedLatLng]);

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <div className="mb-8">
        <div className="flex justify-between items-center mb-4">
          <h1 className="text-4xl font-bold text-gray-900">Admin Dashboard</h1>
          <div className="flex gap-3">
            <button
              onClick={fetchComplaints}
              className="flex items-center gap-2 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
              disabled={loading}
            >
              <RefreshCw className={`w-4 h-4 ${loading ? 'animate-spin' : ''}`} />
              Refresh
            </button>
            <button
              onClick={exportToCSV}
              className="flex items-center gap-2 px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors"
            >
              <Download className="w-4 h-4" />
              Export CSV
            </button>
          </div>
        </div>
        <p className="text-gray-600">Manage and track all complaints in your system</p>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-6 gap-6 mb-8">
        <div className="bg-white p-6 rounded-xl shadow-sm border border-gray-200">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Total</p>
              <p className="text-2xl font-bold text-gray-900">{stats.total}</p>
            </div>
            <div className="w-12 h-12 bg-blue-100 rounded-lg flex items-center justify-center">
              <Building className="w-6 h-6 text-blue-600" />
            </div>
          </div>
        </div>
        <div className="bg-white p-6 rounded-xl shadow-sm border border-gray-200">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Pending</p>
              <p className="text-2xl font-bold text-red-600">{stats.pending}</p>
            </div>
            <div className="w-12 h-12 bg-red-100 rounded-lg flex items-center justify-center">
              <Clock className="w-6 h-6 text-red-600" />
            </div>
          </div>
        </div>
        <div className="bg-white p-6 rounded-xl shadow-sm border border-gray-200">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Processing</p>
              <p className="text-2xl font-bold text-yellow-600">{stats.processing}</p>
            </div>
            <div className="w-12 h-12 bg-yellow-100 rounded-lg flex items-center justify-center">
              <RefreshCw className="w-6 h-6 text-yellow-600" />
            </div>
          </div>
        </div>
        <div className="bg-white p-6 rounded-xl shadow-sm border border-gray-200">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Resolved</p>
              <p className="text-2xl font-bold text-green-600">{stats.resolved}</p>
            </div>
            <div className="w-12 h-12 bg-green-100 rounded-lg flex items-center justify-center">
              <User className="w-6 h-6 text-green-600" />
            </div>
          </div>
        </div>
        <div className="bg-white p-6 rounded-xl shadow-sm border border-gray-200">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Today</p>
              <p className="text-2xl font-bold text-blue-600">{stats.today}</p>
            </div>
            <div className="w-12 h-12 bg-blue-100 rounded-lg flex items-center justify-center">
              <Calendar className="w-6 h-6 text-blue-600" />
            </div>
          </div>
        </div>
        <div className="bg-white p-6 rounded-xl shadow-sm border border-gray-200">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">This Week</p>
              <p className="text-2xl font-bold text-purple-600">{stats.thisWeek}</p>
            </div>
            <div className="w-12 h-12 bg-purple-100 rounded-lg flex items-center justify-center">
              <MapPin className="w-6 h-6 text-purple-600" />
            </div>
          </div>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 xl:grid-cols-4 gap-6 mb-8">
        <CasesByDepartment complaints={complaints} />
        <CasesByCity complaints={complaints} />
        <MonthlyTrend complaints={complaints} />
        <TopCities complaints={complaints} />
      </div>

      <div className="bg-white p-6 rounded-xl shadow-sm border border-gray-200 mb-6">
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
            <input
              type="text"
              placeholder="Search complaints..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            />
          </div>
          <select value={statusFilter} onChange={(e) => setStatusFilter(e.target.value)} className="px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent">
            <option value="All">All Status</option>
            <option value="Pending">Pending</option>
            <option value="Processing">Processing</option>
            <option value="Resolved">Resolved</option>
          </select>
          <select value={departmentFilter} onChange={(e) => setDepartmentFilter(e.target.value)} className="px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent">
            <option value="All">All Departments</option>
            {departments.map(dept => <option key={dept} value={dept}>{dept}</option>)}
          </select>
          <select value={dateFilter} onChange={(e) => setDateFilter(e.target.value)} className="px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent">
            <option value="All">All Time</option>
            <option value="Today">Today</option>
            <option value="This Week">This Week</option>
            <option value="This Month">This Month</option>
          </select>
        </div>
      </div>

      <div className="bg-white rounded-xl shadow-sm border border-gray-200 overflow-hidden">
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Complaint Details</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Location & Department</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status & Priority</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Timestamp</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {loading ? (
                <tr><td colSpan="5" className="text-center p-4"><div className="flex justify-center items-center"><RefreshCw className="w-6 h-6 animate-spin text-blue-600" /><span className="ml-2 text-gray-500">Loading...</span></div></td></tr>
              ) : filteredComplaints.length === 0 ? (
                <tr><td colSpan="5" className="text-center p-4 text-gray-500">No complaints found.</td></tr>
              ) : (
                filteredComplaints.map((complaint) => {
                  const ll = normalizeLatLng(complaint.latitude, complaint.longitude);
                  return (
                    <tr key={complaint.id} className="hover:bg-gray-50 transition-colors">
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="flex items-center">
                          <div className="flex-shrink-0 h-10 w-10 bg-blue-100 rounded-full flex items-center justify-center">
                            <User className="w-5 h-5 text-blue-600" />
                          </div>
                          <div className="ml-4">
                            <div className="text-sm font-medium text-gray-900">{complaint.name}</div>
                            <div className="text-xs text-gray-500">ID: {complaint.id}</div>
                            <p className="text-xs text-gray-400 mt-1 truncate max-w-xs">{complaint.description}</p>
                          </div>
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="text-sm text-gray-900 flex items-center">
                          <MapPin className="w-4 h-4 mr-1 text-gray-400" />{complaint.location}
                        </div>
                        {ll.lat !== null && <p className="text-xs text-gray-400 mt-0.5">Lat: {ll.lat.toFixed(5)}, Lng: {ll.lng.toFixed(5)}</p>}
                        <div className="text-sm text-gray-500 flex items-center mt-1">
                          <Building className="w-4 h-4 mr-1 text-gray-400" />{complaint.department || "Unknown"}
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="flex flex-col space-y-2">
                          <select
                            value={complaint.status}
                            onChange={(e) => handleStatusChange(complaint.id, e.target.value)}
                            className={`text-xs px-2 py-1 rounded-full border font-medium ${getStatusColor(complaint.status)}`}
                          >
                            <option value="Pending">Pending</option>
                            <option value="Processing">Processing</option>
                            <option value="Resolved">Resolved</option>
                          </select>
                          {complaint.urgency && (
                            <div className={`flex items-center text-xs px-2 py-1 rounded-full ${getUrgencyColor(complaint.urgency)}`}>
                              {getPriorityIcon(complaint.urgency)}
                              <span className="ml-1">{complaint.urgency}</span>
                            </div>
                          )}
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                        <div>{new Date(complaint.timestamp).toLocaleDateString()}</div>
                        <div className="text-xs text-gray-400">{new Date(complaint.timestamp).toLocaleTimeString()}</div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <button onClick={() => viewComplaint(complaint)} className="flex items-center text-blue-600 hover:text-blue-800">
                          <Eye className="w-4 h-4 mr-1" /> View
                        </button>
                      </td>
                    </tr>
                  );
                })
              )}
            </tbody>
          </table>
        </div>
      </div>

      <div className="mt-4 text-sm text-gray-500 text-center">
        Showing {filteredComplaints.length} of {complaints.length} complaints
      </div>

      {showModal && selectedComplaint && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-xl shadow-xl max-w-4xl w-full max-h-[90vh] overflow-y-auto">
            <div className="p-6">
              <div className="flex justify-between items-start mb-4">
                <h2 className="text-2xl font-bold text-gray-900">Complaint Details</h2>
                <button onClick={() => setShowModal(false)} className="text-gray-400 hover:text-gray-600 text-2xl">Ã—</button>
              </div>

              <div className="space-y-6">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div><label className="block text-sm font-medium text-gray-700 mb-1">Complaint ID</label><p className="text-sm text-gray-900 bg-gray-50 p-2 rounded">{selectedComplaint.id}</p></div>
                  <div><label className="block text-sm font-medium text-gray-700 mb-1">Token</label><p className="text-sm text-gray-900 bg-gray-50 p-2 rounded">{selectedComplaint.token}</p></div>
                  <div><label className="block text-sm font-medium text-gray-700 mb-1">Name</label><p className="text-sm text-gray-900 bg-gray-50 p-2 rounded">{selectedComplaint.name}</p></div>
                  <div><label className="block text-sm font-medium text-gray-700 mb-1">Department</label><p className="text-sm text-gray-900 bg-gray-50 p-2 rounded">{selectedComplaint.department || "Unknown"}</p></div>
                  <div><label className="block text-sm font-medium text-gray-700 mb-1">Status</label><span className={`text-xs px-3 py-1 rounded-full border font-medium ${getStatusColor(selectedComplaint.status)}`}>{selectedComplaint.status}</span></div>
                  <div><label className="block text-sm font-medium text-gray-700 mb-1">Urgency</label><span className={`text-xs px-3 py-1 rounded-full ${getUrgencyColor(selectedComplaint.urgency)}`}>{selectedComplaint.urgency || "Normal"}</span></div>
                  <div className="md:col-span-2"><label className="block text-sm font-medium text-gray-700 mb-1">Timestamp</label><p className="text-sm text-gray-900 bg-gray-50 p-2 rounded">{new Date(selectedComplaint.timestamp).toLocaleString()}</p></div>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Description</label>
                  <div className="text-sm text-gray-900 bg-gray-50 p-3 rounded min-h-[100px] whitespace-pre-wrap">{selectedComplaint.description || "No description provided"}</div>
                </div>

                <div className="border-t pt-4">
                  <h3 className="text-lg font-semibold text-gray-900 mb-3 flex items-center"><MapPin className="w-5 h-5 mr-2 text-blue-600" />Location Details</h3>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">Coordinates</label>
                      <p className="text-sm text-gray-900 bg-gray-50 p-2 rounded">
                        {selectedLatLng.lat !== null ? `${selectedLatLng.lat.toFixed(6)}, ${selectedLatLng.lng.toFixed(6)}` : "Not available"}
                      </p>
                      {selectedLatLng.swapped && <p className="text-[11px] text-orange-600 mt-1">Note: Lat/Lng appeared swapped and were auto-corrected.</p>}
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">Fetched Address</label>
                      <div className="text-sm text-gray-900 bg-gray-50 p-2 rounded min-h-[40px]">
                        {addrLoading && <span className="text-gray-400">Fetching address...</span>}
                        {addrError && <span className="text-red-500">{addrError}</span>}
                        {!addrLoading && !addrError && (revAddress || selectedComplaint.location || "â€”")}
                      </div>
                    </div>
                  </div>

                  <GoogleMapEmbed latitude={selectedLatLng.lat} longitude={selectedLatLng.lng} />

                  {selectedLatLng.lat !== null && (
                    <div className="mt-3">
                      <a href={`http://maps.google.com/maps?q=${selectedLatLng.lat},${selectedLatLng.lng}`} target="_blank" rel="noopener noreferrer" className="inline-flex items-center px-3 py-2 bg-blue-600 text-white text-sm rounded-lg hover:bg-blue-700 transition-colors">
                        <MapPin className="w-4 h-4 mr-1" /> Open in Google Maps
                      </a>
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
