import React, { useState, useRef } from "react";
import axios from "axios";

export default function WomenChildComplaint() {
  const [recording, setRecording] = useState(false);
  const [complaintText, setComplaintText] = useState("");
  const [token, setToken] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const recognitionRef = useRef(null);

  // 🎙️ Speech-to-text
  const handleSpeechToText = () => {
    const SpeechRecognition =
      window.SpeechRecognition || window.webkitSpeechRecognition;

    if (!SpeechRecognition) {
      alert("Speech recognition is not supported in this browser. Please use Chrome.");
      return;
    }

    if (recording) {
      if (recognitionRef.current) {
        try {
          recognitionRef.current.stop();
        } catch (e) {
          console.warn("Recognition already stopped.");
        }
      }
      setRecording(false);
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.lang = "en-US"; // or "hi-IN"
    recognition.continuous = true;
    recognition.interimResults = true;

    recognition.onresult = (event) => {
      let transcript = "";
      for (let i = 0; i < event.results.length; i++) {
        transcript += event.results[i][0].transcript + " ";
      }
      setComplaintText(transcript.trim());
    };

    recognition.onerror = (err) => {
      console.error("Speech Recognition Error:", err);
      if (err.error === "no-speech") {
        alert("No speech detected. Please try again and speak closer to the mic.");
      } else if (err.error === "not-allowed") {
        alert("Microphone permission is blocked. Please allow mic access in your browser settings.");
      }
      setRecording(false);
    };

    recognition.onend = () => setRecording(false);

    recognition.start();
    recognitionRef.current = recognition;
    setRecording(true);
  };

  // 📤 Submit Complaint
  const handleSubmit = async () => {
    if (!complaintText.trim()) {
      alert("Please speak or type your complaint.");
      return;
    }

    setIsSubmitting(true);

    const submitToServer = async (coords) => {
      try {
        const formData = new FormData();
        formData.append("text", complaintText);
        formData.append("category", "women-child");

        if (coords) {
          formData.append("latitude", coords.latitude);
          formData.append("longitude", coords.longitude);
        }

        // Debug logging
        console.log("🔍 Submitting complaint with text:", complaintText);
        console.log("🔍 FormData contents:");
        for (let [key, value] of formData.entries()) {
          console.log(`  ${key}: ${value}`);
        }

        const res = await axios.post(
          "http://localhost:5000/api/complaint/women-child", // ✅ endpoint
          formData,
          { headers: { "Content-Type": "multipart/form-data" } }
        );

        console.log("🔍 Backend response:", res.data);

        if (res.data?.success) {
          setToken(res.data.token);
          setComplaintText("");
          alert(
            `Complaint submitted successfully${coords ? " with location" : ""}!`
          );
        } else {
          alert(res.data?.message || "Failed to submit complaint.");
        }
      } catch (err) {
        console.error("❌ Error submitting complaint:", err);
        alert("Error submitting complaint.");
      } finally {
        setIsSubmitting(false);
      }
    };

    // 📍 Location capture
    if (!navigator.geolocation) {
      await submitToServer(null);
      return;
    }

    navigator.geolocation.getCurrentPosition(
      async (position) => {
        const { latitude, longitude } = position.coords;

        if (!latitude || !longitude || latitude === 0 || longitude === 0) {
          alert("Unable to fetch accurate location. Please try again outdoors.");
          setIsSubmitting(false);
          return;
        }

        await submitToServer({ latitude, longitude });
      },
      async (error) => {
        console.warn("Geolocation error:", error);
        alert("⚠️ Location not available. Please enable GPS for accurate complaint.");
        setIsSubmitting(false);
      },
      { enableHighAccuracy: true, timeout: 15000, maximumAge: 0 }
    );
  };

  // ✅ yeh component ka asli return hai
  return (
    <div className="max-w-xl mx-auto p-6 bg-white rounded-2xl shadow-lg">
      <h2 className="text-2xl font-bold mb-4 text-center text-gray-800">
        👩‍👧 Women & Child Complaint
      </h2>

      <button
        onClick={handleSpeechToText}
        className={`w-full px-4 py-2 rounded-lg mb-3 font-semibold ${
          recording ? "bg-red-500" : "bg-purple-600"
        } text-white`}
      >
        {recording ? "⏹ Stop Speaking" : "🗣 Speak Complaint"}
      </button>

      <textarea
        placeholder="Type or speak your complaint here..."
        value={complaintText}
        onChange={(e) => setComplaintText(e.target.value)}
        className="w-full border rounded-lg p-3 mb-3 h-32 focus:ring-2 focus:ring-blue-400"
      />

      <button
        onClick={handleSubmit}
        disabled={isSubmitting}
        className={`w-full ${
          isSubmitting ? "bg-gray-400" : "bg-green-600"
        } text-white px-4 py-2 rounded-lg font-semibold`}
      >
        {isSubmitting ? "Submitting..." : "✅ Submit Complaint"}
      </button>

      {token && (
        <div className="mt-4 p-4 border rounded-lg bg-gray-100 shadow-sm">
          <p className="font-semibold text-green-700">✅ Complaint Submitted!</p>
          <p className="text-gray-700">
            Your Token Number:{" "}
            <span className="font-mono text-lg text-black">{token}</span>
          </p>
        </div>
      )}

      <p className="text-xs text-gray-500 mt-3">
        Note: Location is captured automatically on submit (works on HTTPS or localhost).
      </p>
    </div>
  );
}
