import React, { useState } from "react";
import axios from "axios";

export default function TrackStatus() {
  const [complaintId, setComplaintId] = useState("");
  const [token, setToken] = useState("");
  const [result, setResult] = useState(null);
  const [error, setError] = useState("");

  const handleTrack = async () => {
    setError("");
    setResult(null);

    try {
      const res = await axios.get("http://localhost:5000/api/complaint/track", {
        params: {
          id: complaintId,
          token: token,
        },
      });

      if (res.data.success) {
        setResult(res.data.data);
      } else {
        setError("Complaint not found.");
      }
    } catch (err) {
      setError("Error fetching complaint: " + err.response?.data?.message || err.message);
    }
  };

  return (
    <div className="max-w-lg mx-auto mt-10 p-6 bg-white shadow rounded">
      <h2 className="text-2xl font-bold mb-4">Track Complaint Status</h2>

      <div className="mb-4">
        <label className="block text-sm font-medium">Complaint ID</label>
        <input
          type="text"
          value={complaintId}
          onChange={(e) => setComplaintId(e.target.value)}
          className="w-full border rounded px-3 py-2 mt-1"
        />
      </div>

      <div className="mb-4">
        <label className="block text-sm font-medium">Token</label>
        <input
          type="text"
          value={token}
          onChange={(e) => setToken(e.target.value)}
          className="w-full border rounded px-3 py-2 mt-1"
        />
      </div>

      <button
        onClick={handleTrack}
        className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
      >
        Track Status
      </button>

      {error && <div className="mt-4 text-red-600">{error}</div>}

      {result && (
        <div className="mt-6 bg-gray-100 p-4 rounded">
          <p><strong>Status:</strong> {result.status}</p>
          <p><strong>Name:</strong> {result.name}</p>
          <p><strong>Location:</strong> {result.location}</p>
          <p><strong>Department:</strong> {result.department}</p>
          <p><strong>Description:</strong> {result.description}</p>
          <p><strong>Date:</strong> {new Date(result.timestamp).toLocaleString()}</p>
        </div>
      )}
    </div>
  );
}
