import React, { useState } from "react";
import axios from "axios";
// We don't need these imports anymore since we're not using PlacesAutocomplete.
// import PlacesAutocomplete, { geocodeByAddress, getLatLng } from "react-places-autocomplete";

export default function ComplaintForm() {
  const [formData, setFormData] = useState({
    name: "",
    complaint: "",
    location: "", // We will now get the location from live coordinates
    urgency: "normal",
    department: "",
  });

  const [photo, setPhoto] = useState(null);
  const [complaintId, setComplaintId] = useState(null);
  const [submitted, setSubmitted] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handlePhotoChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      setPhoto(e.target.files[0]);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    const submitToServer = async (coords) => {
      try {
        const formDataToSend = new FormData();
        Object.keys(formData).forEach((key) => {
          formDataToSend.append(key, formData[key]);
        });

        if (photo) {
          formDataToSend.append("photo", photo);
        }

        if (coords) {
          formDataToSend.append("latitude", coords.latitude);
          formDataToSend.append("longitude", coords.longitude);
        }

        const res = await axios.post("http://localhost:5000/api/complaint", formDataToSend, {
          headers: { "Content-Type": "multipart/form-data" },
        });

        setComplaintId(res.data.complaint.complaintId);
        setSubmitted(true);

        setFormData({
          name: "",
          complaint: "",
          location: "",
          urgency: "normal",
          department: "",
        });
        setPhoto(null);
        alert(`Complaint submitted successfully! Your Complaint ID is: ${res.data.complaint.complaintId}`);
      } catch (err) {
        console.error("Submission failed:", err.message);
        alert("Complaint submission failed. Please try again.");
      } finally {
        setIsSubmitting(false);
      }
    };

    // --- Live Location Capture Logic from the other component ---
    if (!navigator.geolocation) {
      alert("Geolocation is not supported by your browser. Submitting without location.");
      await submitToServer(null);
      return;
    }

    navigator.geolocation.getCurrentPosition(
      async (position) => {
        const { latitude, longitude } = position.coords;
        await submitToServer({ latitude, longitude });
      },
      async (error) => {
        console.warn("Geolocation error:", error);
        alert("⚠ Location not available. Submitting without location data.");
        await submitToServer(null);
      },
      { enableHighAccuracy: true, timeout: 15000, maximumAge: 0 }
    );
  };

  return (
    <div className="max-w-xl mx-auto mt-10 p-6 bg-white rounded shadow">
      <h2 className="text-2xl font-semibold mb-4">Complaint Form</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          name="name"
          value={formData.name}
          onChange={handleChange}
          placeholder="Your Name"
          required
          className="w-full border px-3 py-2 rounded"
        />
        <textarea
          name="complaint"
          value={formData.complaint}
          onChange={handleChange}
          placeholder="Describe your complaint"
          required
          className="w-full border px-3 py-2 rounded"
        />

        {/* Since we're using live location, we no longer need the Places Autocomplete input */}
        <p className="text-sm text-gray-500">Your location will be automatically captured upon submission.</p>

        {/* Department */}
        <select
          name="department"
          value={formData.department}
          onChange={handleChange}
          className="w-full border px-3 py-2 rounded"
          required
        >
          <option value="">Select a Department</option>
          <option value="Public Works">Public Works</option>
          <option value="Water Supply">Water Supply</option>
          <option value="Sanitation">Sanitation</option>
          <option value="Electricity">Electricity</option>
          <option value="Other">Other</option>
        </select>

        {/* Urgency */}
        <select
          name="urgency"
          value={formData.urgency}
          onChange={handleChange}
          className="w-full border px-3 py-2 rounded"
        >
          <option value="normal">Normal</option>
          <option value="urgent">Urgent</option>
        </select>

        {/* Photo Upload */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Upload / Capture Photo
          </label>
          <input
            type="file"
            accept="image/*"
            capture="environment"
            onChange={handlePhotoChange}
            className="w-full border px-3 py-2 rounded"
          />
          {photo && (
            <p className="text-xs text-gray-500 mt-1">Selected: {photo.name}</p>
          )}
        </div>

        <button
          type="submit"
          disabled={isSubmitting}
          className={`w-full ${
            isSubmitting ? "bg-gray-400" : "bg-blue-600"
          } text-white px-4 py-2 rounded font-semibold`}
        >
          {isSubmitting ? "Submitting..." : "Submit Complaint"}
        </button>
      </form>

      {submitted && (
        <div className="mt-6 text-green-700 font-semibold">
          Complaint submitted successfully!<br />
          Your Complaint ID: <span className="font-bold">{complaintId}</span>
        </div>
      )}
    </div>
  );
}
