import React, { useState, useRef } from "react";
import axios from "axios";

export default function ComplaintForm() {
  const [recording, setRecording] = useState(false);
  const [complaintText, setComplaintText] = useState("");
  const [token, setToken] = useState(null);
  const [department, setDepartment] = useState(""); // üëà Selected department

  const recognitionRef = useRef(null);

  // üó£Ô∏è Speech-to-Text
  const handleSpeechToText = () => {
    const SpeechRecognition =
      window.SpeechRecognition || window.webkitSpeechRecognition;

    if (!SpeechRecognition) {
      alert("‚ùå Speech recognition not supported in this browser. Use Chrome.");
      return;
    }

    if (recording && recognitionRef.current) {
      recognitionRef.current.stop();
      setRecording(false);
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.lang = "en-US"; // üëà English
    recognition.continuous = true;
    recognition.interimResults = true;

    recognition.onresult = (event) => {
      let transcript = "";
      for (let i = 0; i < event.results.length; ++i) {
        transcript += event.results[i][0].transcript + " ";
      }
      setComplaintText(transcript.trim());
    };

    recognition.onerror = (err) => {
      console.error("Speech Recognition Error:", err);
      if (err.error === "no-speech") {
        alert("‚ùå No speech detected. Please try again.");
      } else {
        alert("Speech recognition error: " + err.error);
      }
      setRecording(false);
    };

    recognition.onend = () => {
      setRecording(false);
    };

    recognition.start();
    recognitionRef.current = recognition;
    setRecording(true);
  };

  // üì§ Submit Complaint
  const handleSubmit = async () => {
    if (!department) {
      alert("Please select a department.");
      return;
    }
    if (!complaintText) {
      alert("Please speak or type your complaint.");
      return;
    }

    try {
      const formData = new FormData();
      formData.append("category", department); // üëà department dynamically
      formData.append("text", complaintText);

      const res = await axios.post("http://localhost:5000/api/complaint", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      if (res.data.success) {
        setToken(res.data.token);
        setComplaintText("");
        setDepartment(""); // reset dept
        alert("‚úÖ Complaint submitted successfully!");
      }
    } catch (err) {
      console.error(err);
      alert("‚ùå Error submitting complaint");
    }
  };

  return (
    <div className="max-w-xl mx-auto p-6 bg-white rounded-2xl shadow-lg">
      <h2 className="text-2xl font-bold mb-4 text-center text-gray-800">
        üìù Register Complaint
      </h2>

      {/* üè¢ Select Department */}
      <select
        value={department}
        onChange={(e) => setDepartment(e.target.value)}
        className="w-full border rounded-lg p-3 mb-3 focus:ring-2 focus:ring-blue-400"
      >
        <option value="">-- Select Department --</option>
        <option value="police">üöî Police</option>
        <option value="women-child">üë©‚Äçüëß Women & Child</option>
        <option value="fire">üî• Fire Department</option>
        <option value="electricity">‚ö° Electricity</option>
      </select>

      {/* üé§ Speak Button */}
      <button
        onClick={handleSpeechToText}
        className={`w-full px-4 py-2 rounded-lg mb-3 font-semibold ${
          recording ? "bg-red-500" : "bg-purple-600"
        } text-white`}
      >
        {recording ? "‚èπ Stop Speaking" : "üé§ Speak Complaint"}
      </button>

      {/* üìù Complaint Box */}
      <textarea
        placeholder="Type or speak your complaint here..."
        value={complaintText}
        onChange={(e) => setComplaintText(e.target.value)}
        className="w-full border rounded-lg p-3 mb-3 h-32 focus:ring-2 focus:ring-blue-400"
      />

      {/* üì§ Submit */}
      <button
        onClick={handleSubmit}
        className="w-full bg-green-600 text-white px-4 py-2 rounded-lg font-semibold"
      >
        ‚úÖ Submit Complaint
      </button>

      {/* üé´ Token Display */}
      {token && (
        <div className="mt-4 p-4 border rounded-lg bg-gray-100 shadow-sm">
          <p className="font-semibold text-green-700">‚úÖ Complaint Submitted!</p>
          <p className="text-gray-700">
            Your Token Number:{" "}
            <span className="font-mono text-lg text-black">{token}</span>
          </p>
        </div>
      )}
    </div>
  );
}
