import React, { useState } from "react";
import axios from "axios";

export default function WomenChildComplaint() {
  const [recording, setRecording] = useState(false);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [audioURL, setAudioURL] = useState(null);
  const [complaintText, setComplaintText] = useState("");
  const [token, setToken] = useState(null);

  // üé§ Start Recording
  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const recorder = new MediaRecorder(stream);
      const chunks = [];

      recorder.ondataavailable = (e) => chunks.push(e.data);
      recorder.onstop = () => {
        const blob = new Blob(chunks, { type: "audio/wav" });
        const url = URL.createObjectURL(blob);
        setAudioURL(url);
      };

      recorder.start();
      setMediaRecorder(recorder);
      setRecording(true);
    } catch (err) {
      alert("Mic permission required!");
    }
  };

  // ‚èπ Stop Recording
  const stopRecording = () => {
    if (mediaRecorder) {
      mediaRecorder.stop();
      setRecording(false);
    }
  };

  // üì© Submit Complaint
  const handleSubmit = async () => {
    try {
      const formData = new FormData();
      if (audioURL) {
        const response = await fetch(audioURL);
        const blob = await response.blob();
        formData.append("audio", blob, "complaint.wav");
      }
      if (complaintText) {
        formData.append("text", complaintText);
      }
      formData.append("category", "women-child");

      const res = await axios.post("/api/voice-complaint", formData);

      // assume backend returns { token: "XYZ123" }
      setToken(res.data.token);
      setComplaintText("");
      setAudioURL(null);
      alert("Complaint submitted successfully!");
    } catch (err) {
      console.error(err);
      alert("Error submitting complaint");
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-pink-50 via-red-50 to-rose-100 px-6 py-12">
      <div className="bg-white shadow-xl rounded-2xl w-full max-w-3xl p-10 border border-pink-100">
        <h1 className="text-3xl font-bold text-center text-pink-600 mb-6">
          Women & Child Complaint
        </h1>
        <p className="text-center text-gray-600 mb-6">
          ‡§Ü‡§™ ‡§Ö‡§™‡§®‡•Ä ‡§∂‡§ø‡§ï‡§æ‡§Ø‡§§ ‡§¨‡•ã‡§≤‡§ï‡§∞ ‡§Ø‡§æ ‡§≤‡§ø‡§ñ‡§ï‡§∞ ‡§¶‡§∞‡•ç‡§ú ‡§ï‡§∞ ‡§∏‡§ï‡§§‡•á ‡§π‡•à‡§Ç
        </p>

        {/* üé§ Mic Controls */}
        <div className="flex justify-center mb-6">
          {!recording ? (
            <button
              onClick={startRecording}
              className="bg-pink-500 text-white px-6 py-3 rounded-full shadow-lg hover:bg-pink-600 transition"
            >
              üé§ Start Recording
            </button>
          ) : (
            <button
              onClick={stopRecording}
              className="bg-red-500 text-white px-6 py-3 rounded-full shadow-lg hover:bg-red-600 transition"
            >
              ‚èπ Stop Recording
            </button>
          )}
        </div>

        {/* ‚ñ∂ Playback */}
        {audioURL && (
          <div className="mb-6 text-center">
            <audio controls src={audioURL} className="mx-auto"></audio>
          </div>
        )}

        {/* üìù Text Complaint */}
        <textarea
          className="w-full border rounded-lg p-4 mb-6 focus:ring-2 focus:ring-pink-400 focus:outline-none"
          rows="5"
          placeholder="Type your complaint here..."
          value={complaintText}
          onChange={(e) => setComplaintText(e.target.value)}
        ></textarea>

        {/* üì© Submit */}
        <button
          onClick={handleSubmit}
          className="w-full bg-pink-600 text-white py-3 rounded-lg font-semibold hover:bg-pink-700 transition"
        >
          Submit Complaint
        </button>

        {/* üé´ Token Display */}
        {token && (
          <div className="mt-6 text-center">
            <p className="text-lg font-semibold text-green-600">
              Your Complaint Token: <span className="font-mono">{token}</span>
            </p>
            <p className="text-gray-500 text-sm mt-1">
              Save this token to track your complaint status.
            </p>
          </div>
        )}
      </div>
    </div>
  );
}
