import React, { useState } from "react";
import axios from "axios";

export default function TrackStatus() {
  const [complaintId, setComplaintId] = useState("");
  const [result, setResult] = useState(null);
  const [error, setError] = useState("");

  const handleTrack = async () => {
    if (!complaintId.trim()) return;

    try {
      const res = await axios.post("http://localhost:5000/api/complaint/status/json", {
        complaintId: complaintId.trim(),
      });

      if (res.data.success) {
        setResult(res.data.details);
        setError("");
      } else {
        setResult(null);
        setError("शिकायत नहीं मिली। कृपया सही Complaint ID दर्ज करें।");
      }
    } catch (err) {
      setResult(null);
      setError("सर्वर से कनेक्ट नहीं हो सका या शिकायत नहीं मिली।");
    }
  };

  return (
    <div className="max-w-md mx-auto mt-12 p-4 border rounded bg-white shadow">
      <h2 className="text-xl font-bold mb-4 text-center">शिकायत की स्थिति ट्रैक करें</h2>

      <input
        type="text"
        placeholder="Complaint ID दर्ज करें"
        className="w-full border p-2 rounded mb-4"
        value={complaintId}
        onChange={(e) => setComplaintId(e.target.value)}
      />

      <button
        onClick={handleTrack}
        className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700"
      >
        ट्रैक करें
      </button>

      {error && <p className="text-red-500 mt-4 text-center">{error}</p>}

      {result && (
        <div className="mt-6 border-t pt-4">
          <p><strong>स्थिति:</strong> {result.status}</p>
          <p><strong>शिकायतकर्ता:</strong> {result.name}</p>
          <p><strong>स्थान:</strong> {result.location}</p>
          <p><strong>विभाग:</strong> {result.department}</p>
          <p><strong>विवरण:</strong> {result.description}</p>
        </div>
      )}
    </div>
  );
}
