import React, { useState } from "react";
import axios from "axios";

export default function WomenChildComplaint() {
  const [recording, setRecording] = useState(false);
  const [complaintText, setComplaintText] = useState("");

  const handleVoiceRecord = () => {
    // yahan VoiceComplaint.jsx se record code reuse karo
    // abhi placeholder
    setRecording(!recording);
  };

  const handleSubmit = async () => {
    try {
      const formData = new FormData();
      if (complaintText) {
        formData.append("text", complaintText);
      }
      formData.append("category", "women-child");

      await axios.post("/api/voice-complaint", formData);
      alert("Complaint submitted successfully!");
      setComplaintText("");
    } catch (err) {
      console.error(err);
      alert("Error submitting complaint");
    }
  };

  return (
    <div className="page-container">
      <h2>Women & Child Complaint</h2>

      <button onClick={handleVoiceRecord}>
        {recording ? "‚èπ Stop Recording" : "üé§ Start Recording"}
      </button>

      <textarea
        placeholder="Type your complaint here..."
        value={complaintText}
        onChange={(e) => setComplaintText(e.target.value)}
        className="textarea"
      />

      <button onClick={handleSubmit} className="submit-btn">
        Submit Complaint
      </button>
    </div>
  );
}
